// â”€â”€ Hero entrance â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
if (!reduced) {
  // Staggered entrance â€” calm and deliberate, like a photograph developing
  gsap.set('.hero-eyebrow', { opacity: 0 });
  gsap.set('.hero-h1', { opacity: 0, y: 20 });
  gsap.set('.hf-img--main', { opacity: 0, scale: 1.03 });
  gsap.set('.hf-caption', { opacity: 0 });
  gsap.set('.hero-scroll-hint', { opacity: 0 });

  gsap.timeline({ delay: 0.5, defaults: { ease: 'power2.out' } })
    .to('.hero-eyebrow',     { opacity: 1, duration: 1 })
    .to('.hero-h1',          { opacity: 1, y: 0, duration: 1.4, ease: 'expo.out' }, '-=0.5')
    .to('.hf-img--main',     { opacity: 1, scale: 1, duration: 1.6 }, '-=1')
    .to('.hf-caption',       { opacity: 1, duration: 0.8 }, '-=0.4')
    .to('.hero-scroll-hint', { opacity: 1, duration: 0.8 }, '-=0.3');
}

// â”€â”€ Photo grid â€” staggered reveal â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
document.querySelectorAll('.photo-item').forEach((item, i) => {
  if (!reduced) {
    gsap.set(item, { opacity: 0 });
    gsap.to(item, {
      opacity: 1,
      duration: 1.2,
      ease: 'power1.out',
      delay: (i % 3) * 0.15,
      scrollTrigger: {
        trigger: item,
        start: 'top 85%',
        toggleActions: 'play none none none',
      },
    });
  }
});

// â”€â”€ Parallax on masonry images â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
if (!reduced) {
  document.querySelectorAll('.pi-img').forEach((img, i) => {
    const speed = 0.05 + (i % 3) * 0.02;
    gsap.to(img, {
      yPercent: -8 * speed * 10,
      ease: 'none',
      scrollTrigger: {
        trigger: img.closest('.photo-item'),
        start: 'top bottom',
        end: 'bottom top',
        scrub: 1.5,
      },
    });
  });
}

// â”€â”€ Series items reveal â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
document.querySelectorAll('.series-item').forEach((item, i) => {
  if (!reduced) {
    gsap.set(item, { opacity: 0, x: -15 });
    gsap.to(item, {
      opacity: 1,
      x: 0,
      duration: 1,
      ease: 'expo.out',
      delay: i * 0.12,
      scrollTrigger: {
        trigger: '.series-list',
        start: 'top 75%',
        toggleActions: 'play none none reverse',
      },
    });

    // Reveal preview images staggered
    const imgs = item.querySelectorAll('.si-img');
    gsap.set(imgs, { opacity: 0, y: 10 });
    gsap.to(imgs, {
      opacity: 1,
      y: 0,
      duration: 0.6,
      stagger: 0.08,
      ease: 'power2.out',
      delay: 0.4 + i * 0.12,
      scrollTrigger: {
        trigger: '.series-list',
        start: 'top 75%',
        toggleActions: 'play none none reverse',
      },
    });
  }
});

// â”€â”€ Italic number animation on series â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
if (!reduced) {
  document.querySelectorAll('.si-number').forEach((num) => {
    num.addEventListener('mouseenter', () => {
      gsap.to(num, { color: 'rgba(200, 169, 110, 0.4)', duration: 0.4, ease: 'power2.out' });
    });
    num.addEventListener('mouseleave', () => {
      gsap.to(num, { color: 'rgba(58, 52, 42, 1)', duration: 0.4, ease: 'power2.out' });
    });
  });
}

// â”€â”€ Info section reveal â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
if (!reduced) {
  gsap.set('.info-h2', { opacity: 0, y: 25 });
  gsap.to('.info-h2', {
    opacity: 1,
    y: 0,
    duration: 1.2,
    ease: 'expo.out',
    scrollTrigger: {
      trigger: '.info-section',
      start: 'top 70%',
      toggleActions: 'play none none reverse',
    },
  });

  gsap.set('.info-bio', { opacity: 0, y: 18 });
  gsap.to('.info-bio', {
    opacity: 1,
    y: 0,
    duration: 1,
    stagger: 0.15,
    ease: 'expo.out',
    delay: 0.2,
    scrollTrigger: {
      trigger: '.info-right',
      start: 'top 75%',
      toggleActions: 'play none none reverse',
    },
  });

  // Stats count up
  document.querySelectorAll('.is-num').forEach((el) => {
    const text = el.textContent.trim();
    const hasPlus = text.includes('+');
    const target = parseFloat(text);

    ScrollTrigger.create({
      trigger: '.info-stats',
      start: 'top 80%',
      end: 'top 35%',
      onUpdate: (self) => {
        const val = Math.round(target * self.progress);
        el.textContent = val + (hasPlus ? '+' : '');
      },
    });
  });

  // Portrait fade in
  gsap.set('.ip-img', { opacity: 0, scale: 1.02 });
  gsap.to('.ip-img', {
    opacity: 1,
    scale: 1,
    duration: 1.4,
    ease: 'power2.out',
    scrollTrigger: {
      trigger: '.info-portrait',
      start: 'top 80%',
      toggleActions: 'play none none reverse',
    },
  });
}

// â”€â”€ Contact section reveal â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
if (!reduced) {
  gsap.set('.contact-h2', { opacity: 0, y: 30 });
  gsap.to('.contact-h2', {
    opacity: 1,
    y: 0,
    duration: 1.2,
    ease: 'expo.out',
    scrollTrigger: {
      trigger: '.contact-section',
      start: 'top 70%',
      toggleActions: 'play none none reverse',
    },
  });

  gsap.set('.cl-item', { opacity: 0, y: 12 });
  gsap.to('.cl-item', {
    opacity: 1,
    y: 0,
    duration: 0.8,
    stagger: 0.1,
    ease: 'expo.out',
    delay: 0.2,
    scrollTrigger: {
      trigger: '.contact-body',
      start: 'top 75%',
      toggleActions: 'play none none reverse',
    },
  });

  gsap.set('.contact-availability > *', { opacity: 0, y: 12 });
  gsap.to('.contact-availability > *', {
    opacity: 1,
    y: 0,
    duration: 0.8,
    stagger: 0.12,
    ease: 'expo.out',
    delay: 0.35,
    scrollTrigger: {
      trigger: '.contact-body',
      start: 'top 75%',
      toggleActions: 'play none none reverse',
    },
  });
}

// â”€â”€ Slow scrollâ€driven parallax on hero image â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
if (!reduced) {
  gsap.to('.hf-img--main', {
    yPercent: -6,
    ease: 'none',
    scrollTrigger: {
      trigger: '.hero',
      start: 'top top',
      end: 'bottom top',
      scrub: 2,
    },
  });
}

// â”€â”€ Section label fade-in â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
if (!reduced) {
  document.querySelectorAll('.section-label').forEach((label) => {
    gsap.set(label, { opacity: 0 });
    gsap.to(label, {
      opacity: 1,
      duration: 1,
      ease: 'power1.out',
      scrollTrigger: {
        trigger: label,
        start: 'top 85%',
        toggleActions: 'play none none reverse',
      },
    });
  });
}

// â”€â”€ Motion preference toggle â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
window.addEventListener('motion-preference', (e) => {
  if (e.detail.reduced) {
    gsap.globalTimeline.paused(true);
  } else {
    gsap.globalTimeline.paused(false);
  }
});

