.nav-item::before {
  content: '';
  position: absolute;
  inset: 0;
  background: rgba(255, 255, 255, 0.15);
  border-radius: 20px;
  opacity: 0;
  transition: opacity 0.3s ease;
  z-index: -1;
}

.nav-item:hover::before,
.nav-item.active::before {
  opacity: 1;
}

.nav-right {
  display: flex;
  align-items: center;
  gap: 24px;
}

.nav-login {
  color: var(--text);
  text-decoration: none;
  font-size: 0.95rem;
  font-weight: 400;
  transition: opacity 0.3s ease;
}

.nav-login:hover {
