  --font-display: 'Impact', 'Anton', 'Arial Black', system-ui;
  --font-body:    'Inter', 'Helvetica Neue', Arial, sans-serif;

  --ease-expo: cubic-bezier(0.19, 1, 0.22, 1);
}

/* â”€â”€ Reset / Base â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

html {
  scroll-behavior: auto; /* Lenis handles smooth scrolling */
  font-size: 16px;
}

body {
  background: var(--bg);
  color: var(--text);
  font-family: var(--font-body);
  overflow-x: hidden;
  line-height: 1.5;
}

a { color: inherit; text-decoration: none; }

/* â”€â”€ Section Base â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.section {
  position: relative;
  padding: clamp(4rem, 10vw, 8rem) clamp(1.5rem, 6vw, 6rem);
}

.section-title {
  font-family: var(--font-display);
  font-size: clamp(2rem, 6vw, 4.5rem);
  letter-spacing: 0.04em;
  line-height: 1;
  text-transform: uppercase;
  color: var(--text);
}

/* â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
   HERO
   â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â• */
.hero {
  position: relative;
  min-height: 100dvh;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  padding: 0 clamp(1.5rem, 6vw, 6rem);
  border-bottom: 1px solid var(--border);
}

/* Canvas background â€” full cover */
.hero-canvas {
  position: absolute;
  inset: 0;
  width: 100%;
  height: 100%;
  display: block;
  pointer-events: none;
  z-index: 0;
}

.hero-content {
  position: relative;
  z-index: 1;
  text-align: center;
  max-width: 1400px;
  width: 100%;
}

.hero-eyebrow {
  font-family: var(--font-body);
  font-size: clamp(0.7rem, 1.5vw, 0.9rem);
  letter-spacing: 0.35em;
  text-transform: uppercase;
  color: var(--accent-3);
  margin-bottom: 1.5rem;
  opacity: 0;               /* animated in via GSAP */
}

/* â”€â”€ HERO NAME â€” glitch wrapper â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.hero-name-wrapper {
  position: relative;
  display: inline-block;
}

.hero-name {
  font-family: var(--font-display);
  font-size: clamp(4.5rem, 15vw, 13rem);
  letter-spacing: -0.04em;
  line-height: 0.88;
  text-transform: uppercase;
  color: var(--text);
  cursor: default;
  position: relative;
  display: block;
  opacity: 0;               /* animated in via GSAP */
  /* Paint isolation for clip-path glitch */
  isolation: isolate;
}

/* â”€â”€ Glitch layers â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.hero-name-glitch {
  position: absolute;
  inset: 0;
  font-family: var(--font-display);
  font-size: clamp(4.5rem, 15vw, 13rem);
  letter-spacing: -0.04em;
  line-height: 0.88;
  text-transform: uppercase;
  pointer-events: none;
  opacity: 0;
  white-space: nowrap;
}

/* Glitch only activates when .hero-name-wrapper has .is-hovered */
.hero-name-wrapper.is-hovered .hero-name {
  animation: glitch-main 0.4s steps(1) infinite;
}
.hero-name-wrapper.is-hovered .hero-name-glitch--1 {
  opacity: 1;
  animation: glitch-layer1 0.4s steps(1) infinite;
  color: var(--accent-1);
  mix-blend-mode: screen;
}
.hero-name-wrapper.is-hovered .hero-name-glitch--2 {
  opacity: 1;
  animation: glitch-layer2 0.4s steps(1) infinite;
  color: var(--accent-3);
  mix-blend-mode: screen;
}

@keyframes glitch-main {
  0%   { clip-path: inset(0 0 90% 0); transform: translateX(-3px); }
  25%  { clip-path: inset(30% 0 50% 0); transform: translateX(3px); }
  50%  { clip-path: inset(60% 0 20% 0); transform: translateX(-2px); }
  75%  { clip-path: inset(80% 0 0 0); transform: translateX(2px); }
  100% { clip-path: none; transform: none; }
}

@keyframes glitch-layer1 {
  0%   { clip-path: inset(40% 0 30% 0); transform: translateX(6px); }
  33%  { clip-path: inset(70% 0 10% 0); transform: translateX(-4px); }
  66%  { clip-path: inset(10% 0 60% 0); transform: translateX(5px); }
  100% { clip-path: inset(50% 0 20% 0); transform: translateX(-3px); }
}

@keyframes glitch-layer2 {
  0%   { clip-path: inset(20% 0 60% 0); transform: translateX(-5px) skewX(-2deg); }
  40%  { clip-path: inset(65% 0 5% 0);  transform: translateX(4px) skewX(1deg); }
  70%  { clip-path: inset(0 0 75% 0);   transform: translateX(-2px) skewX(-1deg); }
  100% { clip-path: inset(35% 0 40% 0); transform: translateX(3px) skewX(2deg); }
}

.hero-tagline {
  font-family: var(--font-body);
  font-size: clamp(0.85rem, 2vw, 1.25rem);
  letter-spacing: 0.1em;
  text-transform: uppercase;
  color: var(--muted);
  margin-top: 2rem;
  opacity: 0;               /* animated in via GSAP */
}

.hero-tagline-sep {
  color: var(--accent-1);
  margin: 0 0.6em;
}

/* â”€â”€ Scroll cue â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.hero-scroll-cue {
  position: absolute;
  bottom: clamp(1.5rem, 4vw, 3rem);
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.5rem;
  opacity: 0;
  font-size: 0.65rem;
  letter-spacing: 0.25em;
  text-transform: uppercase;
  color: var(--muted);
}

.hero-scroll-line {
  width: 1px;
  height: 3rem;
  background: linear-gradient(to bottom, var(--muted), transparent);
  animation: scrollPulse 1.8s ease-in-out infinite;
}

@keyframes scrollPulse {
  0%, 100% { opacity: 0.3; transform: scaleY(1); }
  50%       { opacity: 1;   transform: scaleY(1.15); }
}

/* â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
   WORK / GALLERY
   â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â• */
.work-section {
  border-bottom: 1px solid var(--border);
}

.section-header {
  display: flex;
  align-items: baseline;
  justify-content: space-between;
  margin-bottom: clamp(2rem, 5vw, 4rem);
  gap: 1rem;
  flex-wrap: wrap;
}

/* â”€â”€ Layout Toggle Button â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.layout-toggle {
  font-family: var(--font-body);
  font-size: 0.75rem;
  letter-spacing: 0.2em;
  text-transform: uppercase;
  background: transparent;
  border: 1px solid var(--border);
  color: var(--muted);
  padding: 0.5rem 1.25rem;
  cursor: pointer;
  transition: border-color 0.2s, color 0.2s, background 0.2s;
  border-radius: 2px;
}

.layout-toggle:hover,
.layout-toggle:focus-visible {
  border-color: var(--accent-3);
  color: var(--accent-3);
  outline: none;
}

.toggle-sep { margin: 0 0.35em; }

/* Highlight active mode */
.layout-toggle .toggle-label-grid { opacity: 1; }
.layout-toggle .toggle-label-list { opacity: 0.45; }
.layout-toggle.is-list .toggle-label-grid { opacity: 0.45; }
.layout-toggle.is-list .toggle-label-list { opacity: 1; }

/* â”€â”€ Media Gallery â€” Grid layout (default) â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.media-gallery {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: clamp(1rem, 2.5vw, 2rem);
}

/* â”€â”€ Media Card â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.media-card {
  position: relative;
  background: var(--border);
  border: 1px solid var(--border);
  overflow: hidden;
  cursor: pointer;
  /* FLIP needs transform-origin at top-left for cleanest anims */
  transform-origin: top left;
  transition: border-color 0.25s;
  /* Initial state for scroll reveal */
  opacity: 0;
  transform: translateY(24px);
}

.media-card:hover {
  border-color: var(--accent-3);
}

/* â”€â”€ Card colored header bars â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.card-header {
  height: 12px;
  width: 100%;
}
.card-header--1 { background: var(--accent-1); }
.card-header--2 { background: var(--accent-2); }
.card-header--3 { background: var(--accent-3); }
.card-header--4 { background: var(--accent-4); }

/* â”€â”€ Card body â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.card-body {
  padding: clamp(1.25rem, 3vw, 2.5rem);
  min-height: clamp(180px, 22vw, 280px);
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  position: relative;
}

/* â”€â”€ Play button â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.card-play {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -60%);
  color: rgba(255,255,255,0.15);
  transition: color 0.25s, transform 0.35s var(--ease-expo);
}

.media-card:hover .card-play {
  color: rgba(255,255,255,0.85);
  transform: translate(-50%, -60%) scale(1.2);
}

/* â”€â”€ Card meta â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.card-meta {
  margin-top: auto;
}

.card-title {
  font-family: var(--font-display);
  font-size: clamp(1.1rem, 2.5vw, 1.8rem);
  letter-spacing: 0.02em;
  text-transform: uppercase;
  line-height: 1.1;
  margin-bottom: 0.75rem;
  color: var(--text);
}

.card-tags {
  display: flex;
  flex-wrap: wrap;
  gap: 0.4rem;
}

.tag {
  font-size: 0.65rem;
  letter-spacing: 0.18em;
  text-transform: uppercase;
  padding: 0.25em 0.65em;
  border: 1px solid var(--muted);
  color: var(--muted);
  border-radius: 2px;
  transition: border-color 0.2s, color 0.2s;
}

.media-card:hover .tag {
  border-color: rgba(255,255,255,0.3);
  color: rgba(255,255,255,0.6);
}

/* â”€â”€ Card number â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.card-index {
  position: absolute;
  top: clamp(0.75rem, 2vw, 1.5rem);
