.disciplines-marquee:hover {
  --marquee-play-state: paused;
}

/* â”€â”€ Scrolling track â€” one of two identical copies â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.disciplines-track {
