  white-space: nowrap;
  animation: marquee-scroll 28s linear infinite;
  animation-play-state: var(--marquee-play-state);
  will-change: transform;
}

@keyframes marquee-scroll {
  from { transform: translateX(0); }
  to   { transform: translateX(-100%); }
}

