  align-items: center;
  gap: 0.75rem;
  margin-bottom: 0.75rem;
}

.badge {
  display: inline-flex;
  align-items: center;
  padding: 0.25rem 0.75rem;
  background: var(--accent-pale);
  color: var(--accent);
  border-radius: 999px;
  font-size: 0.72rem;
  font-weight: 700;
  letter-spacing: 0.06em;
  text-transform: uppercase;
}

.case-card__year {
  font-size: 0.8rem;
  color: var(--muted);
}

.case-card__title {
  font-family: var(--font-serif);
  font-size: clamp(1.5rem, 2.5vw, 2rem);
  font-weight: 400;
  color: var(--text);
  margin-bottom: 0.6rem;
  line-height: 1.15;
}

.case-card__desc {
  font-size: 0.9rem;
  color: var(--muted);
