.shell {
  position: relative;
  z-index: 1;
  padding: 32px clamp(20px, 6vw, 90px) 90px;
}

.topbar {
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 24px;
  padding: 16px 24px;
  background: var(--panel);
  border: 1px solid var(--line);
  border-radius: 20px;
  backdrop-filter: blur(16px);
}

.brand {
  display: flex;
  align-items: center;
  gap: 16px;
}

.brand-mark {
  width: 48px;
  height: 48px;
  border-radius: 12px;
  background: linear-gradient(140deg, rgba(248, 211, 74, 0.9), rgba(255, 107, 107, 0.9));
  color: #0b0d14;
  display: grid;
  place-items: center;
  font-family: "Press Start 2P", system-ui;
  font-size: 0.7rem;
}

.brand-title {
  font-family: "Press Start 2P", system-ui;
  font-size: 0.85rem;
  margin: 0;
}

.brand-subtitle {
  margin: 6px 0 0;
  color: var(--muted);
  font-size: 0.9rem;
}

.nav {
  display: flex;
  align-items: center;
  gap: 18px;
  font-size: 0.95rem;
}

.nav a {
  color: var(--muted);
  text-transform: uppercase;
  letter-spacing: 0.12em;
  font-size: 0.65rem;
}

.nav-cta {
  padding: 0.5rem 1.1rem;
  border-radius: 999px;
  border: 1px solid rgba(248, 211, 74, 0.6);
  color: var(--text);
  background: rgba(248, 211, 74, 0.1);
}

.hero {
  margin-top: 42px;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(260px, 1fr));
  gap: 32px;
  align-items: center;
}

.hero-copy h1 {
  font-family: "Press Start 2P", system-ui;
  font-size: clamp(1.8rem, 3vw, 2.8rem);
  line-height: 1.3;
  margin-bottom: 20px;
}

.kicker {
  text-transform: uppercase;
  letter-spacing: 0.3em;
  font-size: 0.7rem;
  color: var(--accent);
  margin-bottom: 14px;
}

.lead {
  color: var(--muted);
  max-width: 52ch;
  line-height: 1.6;
}

.hero-actions {
  display: flex;
  gap: 12px;
  margin-top: 24px;
  flex-wrap: wrap;
}

.btn {
  padding: 0.85rem 1.6rem;
  border-radius: 12px;
  border: 1px solid var(--accent);
  background: transparent;
  color: var(--text);
  text-transform: uppercase;
  letter-spacing: 0.2em;
  font-size: 0.65rem;
  cursor: pointer;
}

.btn.primary {
  background: rgba(248, 211, 74, 0.2);
}

.btn.ghost {
  border-color: rgba(115, 246, 255, 0.5);
}

.hero-stats {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
  gap: 12px;
  margin-top: 28px;
}

.hero-stats div {
  background: var(--panel);
  border: 1px solid var(--line);
  border-radius: 14px;
  padding: 12px 16px;
}

.hero-stats span {
  display: block;
  color: var(--muted);
  font-size: 0.8rem;
}

.hero-stats strong {
  font-size: 1.1rem;
}

.hero-stage {
  display: grid;
  place-items: center;
  gap: 14px;
}

#gameboy-stage {
  width: min(360px, 80vw);
  height: min(520px, 60vh);
  border-radius: 28px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  background: rgba(5, 8, 12, 0.8);
  box-shadow: 0 30px 80px rgba(0, 0, 0, 0.6);
  overflow: hidden;
}

.stage-caption {
  text-align: center;
  font-size: 0.85rem;
  color: var(--muted);
}

.stage-caption p {
  margin: 0 0 6px;
  font-family: "Press Start 2P", system-ui;
  font-size: 0.7rem;
  color: var(--accent-3);
}

.section {
  margin-top: 56px;
}

.section-header {
  display: flex;
  justify-content: space-between;
  align-items: baseline;
  text-transform: uppercase;
  letter-spacing: 0.2em;
  font-size: 0.7rem;
  color: var(--muted);
  margin-bottom: 20px;
}

.section-header h2 {
  font-family: "Press Start 2P", system-ui;
  font-size: 1rem;
  margin: 0;
  color: var(--text);
}

.card-grid {
  display: grid;
  gap: 18px;
  grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
}

.card {
  background: var(--panel);
  border: 1px solid var(--line);
  border-radius: 16px;
  padding: 18px;
}

.card h3 {
  margin: 12px 0 8px;
  font-size: 1.1rem;
}

.card p {
  color: var(--muted);
  line-height: 1.5;
}

.card .badge {
  display: inline-flex;
  padding: 4px 8px;
  border-radius: 8px;
  font-size: 0.7rem;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  background: rgba(255, 107, 107, 0.2);
  border: 1px solid rgba(255, 107, 107, 0.5);
  color: var(--accent-2);
}

.card-meta {
  display: flex;
  justify-content: space-between;
  margin-top: 12px;
  color: var(--muted);
  font-size: 0.85rem;
}

.loadout-grid {
  display: grid;
  gap: 16px;
  grid-template-columns: repeat(auto-fit, minmax(160px, 1fr));
}

.loadout-item {
  border: 1px dashed rgba(248, 211, 74, 0.5);
  border-radius: 14px;
  padding: 16px;
  text-align: center;
  background: rgba(9, 12, 20, 0.7);
}

.stats-grid {
  display: grid;
  gap: 16px;
  grid-template-columns: repeat(auto-fit, minmax(160px, 1fr));
}

.stats-grid div {
  background: var(--panel);
  border: 1px solid var(--line);
  border-radius: 16px;
  padding: 16px;
  text-align: center;
}

.stats-grid h3 {
  font-family: "Press Start 2P", system-ui;
  font-size: 1.2rem;
  margin: 0 0 8px;
  color: var(--accent);
}

.tool-grid {
  display: grid;
  gap: 14px;
  grid-template-columns: repeat(auto-fit, minmax(140px, 1fr));
}

.tool-card {
  background: rgba(15, 20, 30, 0.75);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 14px;
  padding: 16px;
  text-align: center;
  font-family: "Press Start 2P", system-ui;
  font-size: 0.65rem;
}

.contact-grid {
  display: grid;
  gap: 16px;
  grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
}

.contact-card {
  display: grid;
  grid-template-columns: auto 1fr;
  gap: 16px;
  align-items: center;
  padding: 16px;
  border-radius: 14px;
  border: 1px solid rgba(255, 255, 255, 0.1);
  background: rgba(10, 14, 22, 0.8);
}

.contact-icon {
  width: 44px;
  height: 44px;
  border-radius: 12px;
  display: grid;
  place-items: center;
  background: rgba(255, 107, 107, 0.2);
  color: var(--accent-2);
  text-transform: uppercase;
  font-family: "Press Start 2P", system-ui;
  font-size: 0.6rem;
}

@media (max-width: 900px) {
  .topbar {
    flex-direction: column;
    align-items: flex-start;
  }

  .nav {
    flex-wrap: wrap;
  }
}

@media (max-width: 640px) {
  .shell {
    padding: 24px 18px 72px;
  }
}

