  margin: 0 0 12px;
}

.hero h1 {
  font-family: "Lettering Gothic", "Oswald", sans-serif;
  font-size: clamp(2.5rem, 5vw, 4rem);
  margin: 0 0 16px;
}

.lead {
  color: var(--muted);
  max-width: 55ch;
}

.actions {
  display: flex;
  gap: 16px;
  margin-top: 24px;
}

.dark-toggle,
.outline {
  padding: 0.85rem 1.6rem;
  border-radius: 999px;
  border: 1px solid var(--red);
  background: transparent;
  color: var(--fg);
  text-transform: uppercase;
  letter-spacing: 0.2em;
  font-size: 0.7rem;
  cursor: pointer;
  position: relative;
  overflow: hidden;
}

.dark-toggle::before {
  content: "";
  position: absolute;
  inset: 0;
  background: var(--red);
  transform: translateX(-100%);
  transition: transform 0.4s ease;
  z-index: 0;
}

.dark-toggle span,
.dark-toggle {
  position: relative;
  z-index: 1;
}

.dark-toggle:hover::before {
  transform: translateX(0);
}

.dark-toggle.active::before {
  transform: translateX(0);
}

.dark-toggle.active {
  color: #050506;
}

.outline {
  border-color: rgba(255, 43, 43, 0.5);
}

.hero-panel {
  background: var(--panel);
  border: 1px solid rgba(255, 43, 43, 0.25);
  border-radius: 18px;
  padding: 24px;
  display: grid;
  gap: 16px;
}

.panel-row {
  display: flex;
  justify-content: space-between;
  color: var(--muted);
}

.panel-row strong {
  color: var(--red);
}

.section {
  padding: 48px 0;
}

.section-header {
  display: flex;
  justify-content: space-between;
  align-items: baseline;
  gap: 16px;
}

.section-header p {
  color: var(--muted);
}

.grid {
  margin-top: 24px;
  display: grid;
  gap: 20px;
  grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
}

.card {
  padding: 20px;
  border-radius: 16px;
  background: var(--panel);
  border: 1px solid rgba(255, 43, 43, 0.2);
}

.card h3 {
  margin: 12px 0 8px;
}

.card p {
  color: var(--muted);
}

.card span {
  font-size: 0.85rem;
  color: var(--red);
  text-transform: uppercase;
  letter-spacing: 0.15em;
}

.split {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
  gap: 24px;
}

.list {
  list-style: none;
  padding: 0;
  margin: 16px 0 0;
  display: grid;
  gap: 12px;
}

.list li {
  padding: 12px 16px;
  border-radius: 12px;
  border: 1px solid rgba(255, 43, 43, 0.2);
  background: rgba(13, 13, 16, 0.7);
}

.pill-grid {
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-top: 16px;
}

.pill-grid span {
  padding: 8px 14px;
  border-radius: 999px;
  border: 1px solid rgba(255, 43, 43, 0.4);
