snippets.css:
:root {
  color-scheme: only light;
  --bg: #07070c;
  --bg-2: #101225;
  --fg: #f5f4f2;
  --muted: rgba(245, 244, 242, 0.7);
  --accent: #73d0ff;
  --ease: cubic-bezier(0.22, 1, 0.36, 1);
}

* {
  box-sizing: border-box;
}

body {
  margin: 0;
  min-height: 100vh;
  font-family: "Rubik", "Segoe UI", sans-serif;
  background:
    radial-gradient(900px 520px at 10% -10%, rgba(115, 208, 255, 0.25), transparent 60%),
    radial-gradient(900px 620px at 90% 0%, rgba(245, 123, 180, 0.2), transparent 60%),
    linear-gradient(150deg, #05050b 0%, #0f1224 55%, #1a1e36 100%);
  color: var(--fg);
}

.page {
  min-height: 100vh;
}

.hero {
  padding: 72px clamp(24px, 5vw, 96px) 40px;
  max-width: 760px;
}

.kicker {
  text-transform: uppercase;
  letter-spacing: 0.38em;
  font-size: 0.8rem;
  color: var(--accent);
  margin: 0 0 16px;
}

.hero h1 {
  font-family: "Cardo", "Times New Roman", serif;
  font-size: clamp(2.6rem, 5vw, 4.4rem);
  margin: 0 0 18px;
}

.lead {
  margin: 0;
  font-size: 1.1rem;
  line-height: 1.7;
  color: var(--muted);
}

.scroll-stage {
  position: relative;
  height: calc((var(--card-count, 4) + 1) * 100vh);
}

.card-stack {
  position: sticky;
  top: 0;
  height: 100vh;
  display: grid;
  place-items: center;
  padding: clamp(24px, 4vw, 52px);
}

.card {
  position: absolute;
  width: min(960px, 92vw);
  min-height: 430px;
  border-radius: 32px;
  overflow: hidden;
  background: rgba(16, 18, 30, 0.78);
  border: 1px solid rgba(255, 255, 255, 0.08);
  box-shadow: 0 30px 90px rgba(5, 6, 12, 0.55);
  display: grid;
  grid-template-columns: minmax(240px, 320px) 1fr;
  gap: clamp(16px, 3vw, 36px);
  padding: clamp(24px, 4vw, 44px);
  opacity: 0;
  transform: translateX(80px) scale(0.96);
  filter: blur(10px);
  clip-path: polygon(0 0, 0 100%, 0 100%, 0 0);
  transition: opacity 0.12s ease, transform 0.12s ease, filter 0.12s ease;
}

.card::before {
  content: "";
  position: absolute;
  inset: 0;
  background: linear-gradient(115deg, rgba(115, 208, 255, 0.22), transparent 55%);
  pointer-events: none;
}

.media {
  position: relative;
  border-radius: 22px;
  min-height: 240px;
  background-size: cover;
  background-position: center;
  box-shadow: inset 0 0 0 1px rgba(255, 255, 255, 0.12);
  transform: translateX(0px) scale(1);
  transition: transform 0.2s var(--ease);
}

.media-sky {
  background-image:
    radial-gradient(circle at 15% 20%, rgba(120, 230, 255, 0.9), transparent 55%),
    radial-gradient(circle at 80% 30%, rgba(120, 255, 198, 0.6), transparent 55%),
    linear-gradient(135deg, #1b2a4a, #263e74);
}

.media-tide {
  background-image:
    radial-gradient(circle at 20% 20%, rgba(255, 192, 118, 0.9), transparent 55%),
    radial-gradient(circle at 75% 40%, rgba(255, 108, 150, 0.7), transparent 60%),
    linear-gradient(135deg, #2a1c2a, #4a2b3b);
}

.media-dune {
  background-image:
    radial-gradient(circle at 30% 25%, rgba(255, 210, 130, 0.95), transparent 60%),
    radial-gradient(circle at 70% 65%, rgba(255, 160, 90, 0.6), transparent 60%),
    linear-gradient(140deg, #3b2616, #6a3b1e);
}

.media-orbit {
  background-image:
    radial-gradient(circle at 20% 25%, rgba(160, 185, 255, 0.9), transparent 55%),
    radial-gradient(circle at 80% 70%, rgba(140, 120, 255, 0.6), transparent 60%),
    linear-gradient(135deg, #121428, #2a2f5e);
}

.media::after {
  content: "";
  position: absolute;
  inset: 0;
