      y: 0,
      opacity: 1,
      duration: dur(0.95),
      ease: 'expo.out',
      stagger: { each: dur(0.06) },
      scrollTrigger: {
        trigger: '.contact__inner',
        start: 'top 80%',
        toggleActions: 'play none none reverse',
      },
    });
  }

  gsap.from('.contact__kicker', {
    opacity: 0,
    y: reduced ? 0 : 16,
    duration: dur(0.7),
    ease: 'expo.out',
    scrollTrigger: {
      trigger: '.contact__inner',
      start: 'top 80%',
      toggleActions: 'play none none reverse',
    },
  });

  gsap.from('.contact__links', {
    opacity: 0,
    y: reduced ? 0 : 20,
    duration: dur(0.8),
    ease: 'expo.out',
    delay: dur(0.35),
    scrollTrigger: {
      trigger: '.contact__inner',
      start: 'top 75%',
      toggleActions: 'play none none reverse',
    },
  });
}

// â”€â”€â”€ Section Headers â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function animateSectionHeaders() {
  document.querySelectorAll('.section__header').forEach((el) => {
    gsap.from(el, {
      opacity: 0,
      y: reduced ? 0 : 16,
      duration: dur(0.65),
      ease: 'expo.out',
      scrollTrigger: {
        trigger: el,
        start: 'top 88%',
        toggleActions: 'play none none reverse',
      },
    });
  });
}

// â”€â”€â”€ Init â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
initHeroCanvas();
animateHero();
animateSectionHeaders();
animateExpertise();
animateTimeline();
animateRepos();
animateContact();

