â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â• */

:root {
  --cream:       #f5f0e8;
  --cream-dark:  #ede5d8;
  --parchment:   #e8ddc8;
  --terracotta:  #c1714a;
  --terracotta-d:#a05a38;
  --brown:       #2c1a0e;
  --brown-mid:   #4a2e1a;
  --gold:        #c9a84c;
  --gold-light:  #e6c97a;
  --charcoal:    #1a1209;
  --warm-gray:   #7a6a58;
  --warm-muted:  #9a8878;

  --font-display: 'Playfair Display', Georgia, serif;
  --font-body:    'Cormorant Garamond', Georgia, serif;
  --font-ui:      'DM Sans', system-ui, sans-serif;
}

*, *::before, *::after { margin: 0; padding: 0; box-sizing: border-box; }

html { scroll-behavior: smooth; }

body {
  font-family: var(--font-body);
  background: var(--cream);
  color: var(--brown);
  overflow-x: hidden;
  font-size: 18px;
  line-height: 1.7;
}

/* â”€â”€ Navigation â”€â”€ */
.nav {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 100;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1.5rem 3rem;
  transition: all 0.4s ease;
}

.nav.scrolled {
  background: rgba(245, 240, 232, 0.95);
  backdrop-filter: blur(12px);
  padding: 1rem 3rem;
  border-bottom: 1px solid rgba(201, 168, 76, 0.2);
}

.nav-logo {
  font-family: var(--font-display);
  font-size: 1.4rem;
  font-weight: 700;
  color: var(--cream);
  letter-spacing: 0.5px;
  transition: color 0.4s ease;
}

.nav.scrolled .nav-logo { color: var(--brown); }

.nav-links {
  list-style: none;
  display: flex;
  gap: 2.5rem;
}

.nav-links a {
  font-family: var(--font-ui);
  font-size: 0.8rem;
  font-weight: 500;
  letter-spacing: 1.5px;
  text-transform: uppercase;
  color: rgba(245, 240, 232, 0.8);
  text-decoration: none;
  transition: color 0.3s ease;
}

.nav.scrolled .nav-links a { color: var(--warm-gray); }

.nav-links a:hover { color: var(--gold); }
.nav.scrolled .nav-links a:hover { color: var(--terracotta); }

.nav-cta {
  font-family: var(--font-ui);
  font-size: 0.75rem;
  font-weight: 500;
  letter-spacing: 1.5px;
  text-transform: uppercase;
  color: var(--gold);
  text-decoration: none;
  border: 1px solid var(--gold);
  padding: 0.5rem 1.2rem;
  border-radius: 1px;
  transition: all 0.3s ease;
}

.nav-cta:hover {
  background: var(--gold);
  color: var(--brown);
}

.nav.scrolled .nav-cta { color: var(--terracotta); border-color: var(--terracotta); }
.nav.scrolled .nav-cta:hover { background: var(--terracotta); color: var(--cream); }

/* â”€â”€ Hero â”€â”€ */
.hero {
  position: relative;
  height: 100vh;
  min-height: 700px;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
}

.hero-image {
  position: absolute;
  inset: 0;
  background:
    linear-gradient(135deg, rgba(193, 113, 74, 0.3) 0%, rgba(44, 26, 14, 0.2) 100%),
    radial-gradient(ellipse at 70% 40%, rgba(201, 168, 76, 0.15) 0%, transparent 60%);
  background-color: var(--brown);

  /* Simulated food photo with CSS gradients */
  background-image:
    radial-gradient(ellipse at 30% 60%, rgba(201, 168, 76, 0.08) 0%, transparent 50%),
    radial-gradient(ellipse at 70% 30%, rgba(193, 113, 74, 0.12) 0%, transparent 50%),
    linear-gradient(160deg, #1a0e06 0%, #2c1a0e 40%, #3a2010 70%, #1a0e06 100%);
}

.hero-overlay {
  position: absolute;
  inset: 0;
  background: linear-gradient(
    to bottom,
    rgba(26, 18, 9, 0.3) 0%,
    rgba(26, 18, 9, 0.1) 40%,
    rgba(26, 18, 9, 0.6) 100%
  );
}

.hero-content {
  position: relative;
  z-index: 2;
  text-align: center;
  max-width: 800px;
  padding: 0 2rem;
}

.hero-pre {
  font-family: var(--font-ui);
  font-size: 0.72rem;
  font-weight: 400;
  letter-spacing: 3px;
  text-transform: uppercase;
  color: var(--gold-light);
  margin-bottom: 2rem;
  opacity: 0.9;
}

.hero-title {
  font-family: var(--font-display);
  font-size: clamp(3.5rem, 8vw, 7rem);
  font-weight: 400;
  line-height: 1.05;
  color: var(--cream);
  margin-bottom: 2rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.1em;
}

.hero-title .line { display: block; overflow: hidden; }

.hero-title .italic {
  font-style: italic;
  color: var(--gold-light);
}

.hero-desc {
  font-family: var(--font-body);
  font-size: 1.15rem;
  font-weight: 300;
  color: rgba(245, 240, 232, 0.75);
  max-width: 500px;
  margin: 0 auto 3rem;
  line-height: 1.8;
}

.hero-actions {
  display: flex;
  gap: 1.5rem;
  justify-content: center;
  flex-wrap: wrap;
}

.btn-gold {
  font-family: var(--font-ui);
  font-size: 0.75rem;
  font-weight: 500;
  letter-spacing: 2px;
  text-transform: uppercase;
  text-decoration: none;
  padding: 1rem 2.5rem;
  background: var(--gold);
  color: var(--brown);
  border: none;
  border-radius: 1px;
  transition: all 0.3s ease;
  display: inline-block;
}

.btn-gold:hover {
  background: var(--gold-light);
  transform: translateY(-1px);
  box-shadow: 0 8px 24px rgba(201, 168, 76, 0.3);
}

.btn-ghost-warm {
  font-family: var(--font-ui);
  font-size: 0.75rem;
  font-weight: 500;
  letter-spacing: 2px;
  text-transform: uppercase;
  text-decoration: none;
  padding: 1rem 2.5rem;
  color: var(--cream);
  border: 1px solid rgba(245, 240, 232, 0.3);
  border-radius: 1px;
  transition: all 0.3s ease;
  display: inline-block;
}

.btn-ghost-warm:hover {
  border-color: var(--gold);
  color: var(--gold);
}

.btn-outline-warm {
  font-family: var(--font-ui);
  font-size: 0.75rem;
  font-weight: 500;
  letter-spacing: 1.5px;
  text-transform: uppercase;
  text-decoration: none;
  padding: 0.8rem 2rem;
  color: var(--terracotta);
  border: 1px solid var(--terracotta);
  border-radius: 1px;
  transition: all 0.3s ease;
  display: inline-block;
}

.btn-outline-warm:hover {
  background: var(--terracotta);
  color: var(--cream);
}

.hero-scroll {
  position: absolute;
  bottom: 3rem;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.8rem;
  z-index: 2;
}

.scroll-line {
  width: 1px;
  height: 50px;
  background: linear-gradient(to bottom, var(--gold), transparent);
  animation: scrollPulse 2s ease-in-out infinite;
}

@keyframes scrollPulse {
  0%, 100% { opacity: 0.4; transform: scaleY(1); }
  50% { opacity: 0.9; transform: scaleY(1.2); }
}

.hero-scroll span {
  font-family: var(--font-ui);
  font-size: 0.6rem;
  letter-spacing: 3px;
  text-transform: uppercase;
