/* â”€â”€ Section Shared â”€â”€ */
.section-label {
  display: block;
  font-family: var(--font-ui);
  font-size: 0.68rem;
  font-weight: 500;
  letter-spacing: 3px;
  text-transform: uppercase;
  color: var(--terracotta);
  margin-bottom: 1.2rem;
}

/* â”€â”€ Philosophy â”€â”€ */
.philosophy-section {
  padding: 8rem 2rem;
  background: var(--brown);
  position: relative;
  overflow: hidden;
}

.philosophy-inner {
  max-width: 900px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 2fr;
  gap: 4rem;
  align-items: center;
}

.philosophy-deco {
  position: relative;
}

.deco-circle {
  width: 200px;
  height: 200px;
  border-radius: 50%;
  border: 1px solid rgba(201, 168, 76, 0.2);
  position: relative;
}

.deco-circle::after {
  content: '';
  position: absolute;
  inset: 20px;
  border-radius: 50%;
  border: 1px solid rgba(201, 168, 76, 0.1);
}

.deco-line-h {
  width: 60px;
  height: 1px;
  background: var(--gold);
  margin-top: 2rem;
  opacity: 0.4;
}

.philosophy-text h2 {
  font-family: var(--font-display);
  font-size: clamp(2rem, 4vw, 3.2rem);
  font-weight: 400;
  color: var(--cream);
  line-height: 1.25;
  margin-bottom: 0.5rem;
}

.philosophy-text h2 em {
  font-style: italic;
  color: var(--gold-light);
}

.attr {
  font-family: var(--font-ui);
  font-size: 0.75rem;
  letter-spacing: 1.5px;
  color: var(--warm-muted);
  margin-bottom: 2rem;
}

.body-text {
  font-family: var(--font-body);
  font-size: 1.05rem;
  font-weight: 300;
  color: rgba(232, 221, 200, 0.7);
  line-height: 1.9;
}

/* â”€â”€ Menu â”€â”€ */
.menu-section {
  padding: 8rem 2rem;
  background: var(--cream-dark);
}

.menu-header {
  text-align: center;
  max-width: 600px;
  margin: 0 auto 5rem;
}

.menu-header h2 {
  font-family: var(--font-display);
  font-size: clamp(2.2rem, 4vw, 3.5rem);
  font-weight: 400;
  color: var(--brown);
  margin-top: 0.5rem;
}

.menu-grid {
  max-width: 1100px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: 0;
}

.menu-item {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 0;
  border-top: 1px solid rgba(44, 26, 14, 0.12);
}

.menu-item:last-child { border-bottom: 1px solid rgba(44, 26, 14, 0.12); }

.menu-item--reverse { direction: rtl; }
.menu-item--reverse > * { direction: ltr; }

.menu-img {
  height: 360px;
  background-color: var(--brown-mid);
  position: relative;
  overflow: hidden;
}

.menu-img--1 {
  background: linear-gradient(135deg, #3a1e0a 0%, #5a2e12 40%, #8a4a22 70%, #3a1e0a 100%);
}
.menu-img--2 {
  background: linear-gradient(135deg, #1a1005 0%, #3a2010 40%, #6a3820 70%, #1a1005 100%);
}
.menu-img--3 {
  background: linear-gradient(135deg, #2a1808 0%, #4a2810 40%, #7a4830 70%, #2a1808 100%);
}

.menu-img::after {
  content: '';
  position: absolute;
  inset: 0;
  background: radial-gradient(ellipse at 50% 50%, rgba(201, 168, 76, 0.08) 0%, transparent 70%);
}

.menu-info {
  padding: 4rem 4rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
  background: var(--cream);
}

.menu-course {
  font-family: var(--font-ui);
  font-size: 0.65rem;
  font-weight: 500;
  letter-spacing: 2.5px;
  text-transform: uppercase;
  color: var(--terracotta);
  margin-bottom: 1rem;
}

.menu-info h3 {
  font-family: var(--font-display);
  font-size: 1.8rem;
  font-weight: 400;
  color: var(--brown);
  margin-bottom: 1rem;
  line-height: 1.2;
}

.menu-info p {
  font-family: var(--font-body);
  font-size: 1rem;
  font-weight: 300;
  color: var(--warm-gray);
  line-height: 1.8;
  margin-bottom: 2rem;
}

.menu-price {
  font-family: var(--font-display);
  font-size: 1.4rem;
  font-weight: 400;
  color: var(--gold);
  letter-spacing: 1px;
}

.menu-footer {
  text-align: center;
  margin-top: 4rem;
  padding-top: 3rem;
  border-top: 1px solid rgba(44, 26, 14, 0.12);
}

.menu-footer p {
  font-family: var(--font-body);
  font-size: 1rem;
  font-style: italic;
  color: var(--warm-gray);
  margin-bottom: 1.5rem;
}

/* â”€â”€ Story â”€â”€ */
.story-section {
  padding: 8rem 2rem;
  background: var(--parchment);
}

.story-grid {
  max-width: 1100px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 6rem;
  align-items: center;
}

.story-images {
  position: relative;
}

.story-img {
  border-radius: 2px;
}

.story-img--main {
  width: 100%;
  height: 500px;
  background: linear-gradient(160deg, #2c1a0e 0%, #4a2e1a 40%, #6a3820 70%, #2c1a0e 100%);
}

.story-img--accent {
  width: 55%;
  height: 200px;
  position: absolute;
  bottom: -3rem;
  right: -3rem;
  background: linear-gradient(160deg, #c1714a 0%, #a05a38 50%, #c1714a 100%);
  border: 4px solid var(--parchment);
}

.story-year {
  position: absolute;
  top: 2rem;
  left: -2rem;
