import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import { SplitText } from 'gsap/SplitText';
import Lenis from 'lenis';

gsap.registerPlugin(ScrollTrigger, SplitText);

initDemoShell({ title: 'Fine Dining Restaurant', category: 'pages', tech: ['gsap', 'splittext', 'scrolltrigger', 'lenis'] });

const lenis = new Lenis({ lerp: 0.08, smoothWheel: true });
lenis.on('scroll', ScrollTrigger.update);
gsap.ticker.add((time) => lenis.raf(time * 1000));
gsap.ticker.lagSmoothing(0);

const reduced = prefersReducedMotion();
if (reduced) document.documentElement.classList.add('reduced-motion');

// â”€â”€ Nav scroll effect â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const nav = document.getElementById('nav');
ScrollTrigger.create({
  start: 'top -80',
  onUpdate: (self) => {
    nav.classList.toggle('scrolled', self.scroll() > 80);
  },
});

// â”€â”€ Hero entrance â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
if (!reduced) {
  gsap.set('.hero-pre', { opacity: 0, y: 15 });
  gsap.set('.hero-desc', { opacity: 0, y: 20 });
  gsap.set('.hero-actions', { opacity: 0, y: 20 });
  gsap.set('.hero-scroll', { opacity: 0 });

  const heroSplit = new SplitText('.hero-title .line', { type: 'chars' });
  gsap.set(heroSplit.chars, { opacity: 0, y: 60, rotationX: -30 });

  const tl = gsap.timeline({ defaults: { ease: 'expo.out' } });
  tl.to('.hero-pre', { opacity: 1, y: 0, duration: 0.9, delay: 0.5 })
    .to(heroSplit.chars, { opacity: 1, y: 0, rotationX: 0, duration: 1.2, stagger: 0.025 }, '-=0.5')
    .to('.hero-desc', { opacity: 1, y: 0, duration: 0.9 }, '-=0.6')
    .to('.hero-actions', { opacity: 1, y: 0, duration: 0.7 }, '-=0.5')
    .to('.hero-scroll', { opacity: 1, duration: 0.6 }, '-=0.2');
}

// â”€â”€ Hero parallax â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
if (!reduced) {
  gsap.to('.hero-image', {
    yPercent: 25,
    ease: 'none',
    scrollTrigger: { trigger: '.hero', start: 'top top', end: 'bottom top', scrub: true },
  });

  gsap.to('.hero-content', {
    yPercent: 15,
    opacity: 0,
    ease: 'none',
    scrollTrigger: { trigger: '.hero', start: '60% top', end: 'bottom top', scrub: true },
  });
}

// â”€â”€ Philosophy â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
if (!reduced) {
  gsap.set('.deco-circle', { scale: 0.6, opacity: 0 });
  gsap.to('.deco-circle', {
    scale: 1, opacity: 1, duration: 1.5, ease: 'expo.out',
    scrollTrigger: { trigger: '.philosophy-section', start: 'top 70%' },
  });

  const philSplit = new SplitText('.philosophy-text h2', { type: 'lines' });
  gsap.set(philSplit.lines, { opacity: 0, y: 40 });
  gsap.to(philSplit.lines, {
    opacity: 1, y: 0, duration: 1, stagger: 0.15, ease: 'expo.out',
    scrollTrigger: { trigger: '.philosophy-text', start: 'top 70%', toggleActions: 'play none none reverse' },
  });

  gsap.set('.attr, .body-text', { opacity: 0, y: 20 });
  gsap.to('.attr, .body-text', {
    opacity: 1, y: 0, duration: 0.8, stagger: 0.2, ease: 'expo.out',
    scrollTrigger: { trigger: '.philosophy-text', start: 'top 65%', toggleActions: 'play none none reverse' },
  });
}

// â”€â”€ Menu items stagger â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
document.querySelectorAll('.menu-item').forEach((item, i) => {
  if (!reduced) {
    gsap.set(item, { opacity: 0, y: 50 });
    gsap.to(item, {
      opacity: 1, y: 0, duration: 1, ease: 'expo.out',
      scrollTrigger: { trigger: item, start: 'top 80%', toggleActions: 'play none none reverse' },
    });

    // Image parallax within item
    const img = item.querySelector('.menu-img');
    if (img) {
      gsap.to(img, {
        yPercent: -10,
        ease: 'none',
        scrollTrigger: { trigger: item, start: 'top bottom', end: 'bottom top', scrub: true },
      });
    }
  }
});

// Menu header
if (!reduced) {
  gsap.set('.menu-header .section-label, .menu-header h2', { opacity: 0, y: 30 });
  gsap.to('.menu-header .section-label, .menu-header h2', {
    opacity: 1, y: 0, duration: 0.9, stagger: 0.15, ease: 'expo.out',
    scrollTrigger: { trigger: '.menu-header', start: 'top 75%', toggleActions: 'play none none reverse' },
  });
}

// â”€â”€ Story section â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
if (!reduced) {
  // Images reveal
  gsap.set('.story-img--main', { opacity: 0, scale: 1.05 });
  gsap.to('.story-img--main', {
    opacity: 1, scale: 1, duration: 1.3, ease: 'expo.out',
    scrollTrigger: { trigger: '.story-grid', start: 'top 70%', toggleActions: 'play none none reverse' },
  });

  gsap.set('.story-img--accent', { opacity: 0, x: 30 });
  gsap.to('.story-img--accent', {
    opacity: 1, x: 0, duration: 1, delay: 0.3, ease: 'expo.out',
    scrollTrigger: { trigger: '.story-grid', start: 'top 70%', toggleActions: 'play none none reverse' },
  });

  // Story year parallax
  gsap.to('.story-year', {
    yPercent: -20,
    ease: 'none',
    scrollTrigger: { trigger: '.story-section', start: 'top bottom', end: 'bottom top', scrub: true },
  });

  // Text reveal
  const storySplit = new SplitText('.story-content h2', { type: 'lines' });
  gsap.set(storySplit.lines, { opacity: 0, y: 30 });
  gsap.to(storySplit.lines, {
    opacity: 1, y: 0, duration: 0.9, stagger: 0.12, ease: 'expo.out',
    scrollTrigger: { trigger: '.story-content', start: 'top 75%', toggleActions: 'play none none reverse' },
  });

  gsap.set('.story-content p', { opacity: 0, y: 20 });
  gsap.to('.story-content p', {
    opacity: 1, y: 0, duration: 0.8, stagger: 0.15, ease: 'expo.out',
    scrollTrigger: { trigger: '.story-content', start: 'top 70%', toggleActions: 'play none none reverse' },
  });

  // Awards counter reveal
  gsap.set('.award', { opacity: 0, y: 25 });
  gsap.to('.award', {
    opacity: 1, y: 0, duration: 0.7, stagger: 0.1, ease: 'back.out(1.5)',
    scrollTrigger: { trigger: '.story-awards', start: 'top 80%', toggleActions: 'play none none reverse' },
  });
}

// â”€â”€ Reservation â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
if (!reduced) {
  gsap.set('.res-inner > *', { opacity: 0, y: 30 });
  gsap.to('.res-inner > *', {
    opacity: 1, y: 0, duration: 0.8, stagger: 0.12, ease: 'expo.out',
    scrollTrigger: { trigger: '.reservation-section', start: 'top 70%', toggleActions: 'play none none reverse' },
  });
}

// â”€â”€ Motion preference â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
window.addEventListener('motion-preference', (e) => {
  gsap.globalTimeline.paused(e.detail.reduced);
});

snippets.css:
:root {
  --bg: #070a12;
  --text: #f0f4fb;
  --panel: #121a2b;
  --border: #263249;
  --accent: #86e8ff;
  --muted: #8a95a8;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
  background: var(--bg);
  color: var(--text);
  line-height: 1.6;
  overflow-x: hidden;
}

.hero {
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 2rem;
  background: linear-gradient(135deg, rgba(134, 232, 255, 0.05) 0%, rgba(174, 82, 255, 0.05) 100%);
}

.hero .content {
  max-width: 600px;
  text-align: center;
}

.eyebrow {
  display: inline-block;
  font-size: 0.75rem;
  font-weight: 700;
  letter-spacing: 2px;
  text-transform: uppercase;
  color: var(--accent);
  margin-bottom: 1rem;
}

.hero h1 {
  font-size: 3.5rem;
  font-weight: 800;
  line-height: 1.1;
  margin-bottom: 1.5rem;
  letter-spacing: -1px;
}

.subtitle {
  font-size: 1.1rem;
  color: var(--muted);
}

/* Cards Container with Perspective */
.cards-container {
  padding: 6rem 2rem;
  background: rgba(18, 26, 43, 0.5);
  perspective: 1200px;
}

.cards-grid {
  max-width: 1200px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 2rem;
  perspective-origin: center center;
}

.card {
  background: var(--panel);
  border: 1px solid var(--border);
  border-radius: 12px;
  padding: 2rem;
  cursor: pointer;
  transition: all 0.4s cubic-bezier(0.34, 1.56, 0.64, 1);
  transform-style: preserve-3d;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
}

.card:hover {
  border-color: var(--accent);
  background: rgba(134, 232, 255, 0.08);
}

.card-inner {
  text-align: center;
  position: relative;
  z-index: 1;
}

.card-icon {
  font-size: 3rem;
  margin-bottom: 1rem;
  color: var(--accent);
  opacity: 0.8;
}

.card h3 {
  font-size: 1.3rem;
  margin-bottom: 1rem;
  color: var(--text);
}

.card p {
  font-size: 0.95rem;
  color: var(--muted);
  line-height: 1.7;
}

/* Details Section */
.details {
  max-width: 800px;
  margin: 4rem auto;
  padding: 4rem 2rem;
  border: 1px solid var(--border);
  border-radius: 12px;
  background: rgba(18, 26, 43, 0.8);
}

.details .content {
  max-width: 800px;
  margin: 0 auto;
}

.details h2 {
  font-size: 1.8rem;
  margin-bottom: 1.5rem;
  color: var(--text);
}

.details h3 {
  font-size: 1.2rem;
  margin-top: 2rem;
  margin-bottom: 0.8rem;
  color: var(--accent);
}

.details p {
  font-size: 1rem;
  color: var(--muted);
  margin-bottom: 1.5rem;
  line-height: 1.8;
}

.details ul {
  margin-left: 1.5rem;
  margin-bottom: 1.5rem;
}

.details li {
  color: var(--muted);
  margin-bottom: 0.8rem;
  line-height: 1.7;
}

.details li strong {
  color: var(--accent);
}

code {
  background: rgba(134, 232, 255, 0.1);
  color: var(--accent);
  padding: 0.2rem 0.5rem;
  border-radius: 4px;
  font-family: 'Monaco', 'Courier New', monospace;
  font-size: 0.9rem;
}

.btn-back {
  display: inline-block;
  margin-top: 2rem;
  padding: 0.8rem 1.5rem;
  background: var(--accent);
  color: var(--bg);
  border: none;
  border-radius: 4px;
  font-weight: 600;
  text-decoration: none;
  cursor: pointer;
  transition: all 0.3s ease;
}

.btn-back:hover {
  background: rgba(134, 232, 255, 0.8);
  transform: translateY(-2px);
}

/* Responsive */
@media (max-width: 768px) {
  .hero h1 {
    font-size: 2.5rem;
  }

  .cards-grid {
    grid-template-columns: repeat(2, 1fr);
    gap: 1.5rem;
  }

  .card {
    padding: 1.5rem;
  }

  .details {
    margin: 2rem auto;
    padding: 2rem 1.5rem;
  }
}

@media (max-width: 480px) {
  .hero h1 {
    font-size: 2rem;
  }

  .subtitle {
    font-size: 1rem;
  }

  .cards-grid {
    grid-template-columns: 1fr;
  }

  .card-icon {
    font-size: 2.5rem;
  }

  .card h3 {
    font-size: 1.1rem;
  }
}

html.reduced-motion .card,
html.reduced-motion .hero {
  animation: none !important;
}

html.reduced-motion * {
  animation-duration: 0.01ms !important;
  animation-iteration-count: 1 !important;
  transition-duration: 0.01ms !important;
}

