@import url('https://fonts.googleapis.com/css2?family=Black+Ops+One&family=Orbitron:wght@400;700;900&family=Rajdhani:wght@300;400;500;600;700&display=swap');

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

:root {
    --pink: #FFB6C1;
    --pink-dark: #FF69B4;
    --pink-light: #FFC0CB;
    --dark-bg: #0a0a0a;
    --dark-bg-2: #1a0a0a;
    --text-white: #ffffff;
    --text-gray: #cccccc;
}

body {
    font-family: 'Rajdhani', sans-serif;
    background: var(--dark-bg);
    color: var(--text-white);
    overflow-x: hidden;
    position: relative;
}

#canvas {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 0;
    pointer-events: none;
}

.container {
    position: relative;
    z-index: 1;
}

/* Navbar */
.navbar {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    padding: 1.5rem 6rem;
    background: rgba(10, 10, 10, 0.8);
    backdrop-filter: blur(10px);
    border-bottom: 1px solid rgba(255, 182, 193, 0.2);
    z-index: 1000;
    transform: translateY(-100%);
    opacity: 0;
    transition: transform 0.6s cubic-bezier(0.4, 0, 0.2, 1), 
                opacity 0.6s ease,
                padding 0.3s ease,
                background 0.3s ease,
                box-shadow 0.3s ease;
}

.navbar.visible {
    transform: translateY(0);
    opacity: 1;
}

.navbar.hidden {
    transform: translateY(-100%);
    opacity: 0;
}

.navbar.scrolled {
    padding: 1rem 6rem;
    background: rgba(10, 10, 10, 0.95);
    box-shadow: 0 5px 20px rgba(0, 0, 0, 0.5);
    border-bottom: 1px solid rgba(255, 182, 193, 0.3);
}

.nav-container {
    display: flex;
    justify-content: space-between;
    align-items: center;
    max-width: 1400px;
    margin: 0 auto;
}

.nav-logo {
    font-family: 'Black Ops One', cursive;
    font-size: 1.5rem;
    font-weight: 400;
    opacity: 0;
    transform: translateX(-30px);
    transition: opacity 0.6s ease 0.2s, transform 0.6s cubic-bezier(0.4, 0, 0.2, 1) 0.2s;
}

.navbar.visible .nav-logo {
    opacity: 1;
    transform: translateX(0);
}

.logo-link {
    color: var(--pink);
    text-decoration: none;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    text-shadow: 
        0 0 10px rgba(255, 182, 193, 0.5),
        2px 2px 0px rgba(0, 0, 0, 0.8);
    transition: all 0.3s ease;
}

.logo-link:hover {
    color: var(--pink-light);
    text-shadow: 
        0 0 20px rgba(255, 182, 193, 0.8),
        2px 2px 0px rgba(0, 0, 0, 0.8);
}

.nav-menu {
    display: flex;
    list-style: none;
    gap: 3rem;
    align-items: center;
    opacity: 0;
    transform: translateX(30px);
    transition: opacity 0.6s ease 0.4s, transform 0.6s cubic-bezier(0.4, 0, 0.2, 1) 0.4s;
}

.navbar.visible .nav-menu {
    opacity: 1;
    transform: translateX(0);
}

.nav-item {
    position: relative;
    opacity: 0;
    transform: translateY(-20px);
    transition: opacity 0.4s ease, transform 0.4s ease;
}

.navbar.visible .nav-item {
    opacity: 1;
    transform: translateY(0);
}

.navbar.visible .nav-item:nth-child(1) { transition-delay: 0.5s; }
.navbar.visible .nav-item:nth-child(2) { transition-delay: 0.6s; }
.navbar.visible .nav-item:nth-child(3) { transition-delay: 0.7s; }
.navbar.visible .nav-item:nth-child(4) { transition-delay: 0.8s; }
.navbar.visible .nav-item:nth-child(5) { transition-delay: 0.9s; }

.nav-link {
    font-family: 'Orbitron', sans-serif;
    font-size: 0.875rem;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.15em;
    color: var(--text-white);
    text-decoration: none;
    padding: 0.5rem 0;
    position: relative;
    transition: color 0.3s ease, transform 0.3s ease;
}

.nav-link::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 0;
    height: 2px;
    background: var(--pink);
    transition: width 0.3s ease;
    box-shadow: 0 0 10px rgba(255, 182, 193, 0.5);
}

.nav-link:hover {
    color: var(--pink);
    transform: translateY(-2px);
}

.nav-link:hover::after {
    width: 100%;
}

.nav-link.active {
    color: var(--pink);
}

.nav-link.active::after {
    width: 100%;
}

.nav-toggle {
    display: none;
    flex-direction: column;
    cursor: pointer;
    gap: 5px;
    opacity: 0;
    transform: scale(0.8);
    transition: opacity 0.6s ease 0.6s, transform 0.6s cubic-bezier(0.4, 0, 0.2, 1) 0.6s;
}

.navbar.visible .nav-toggle {
    opacity: 1;
    transform: scale(1);
}

.bar {
    width: 25px;
    height: 3px;
    background: var(--pink);
    transition: all 0.3s ease;
    box-shadow: 0 0 5px rgba(255, 182, 193, 0.5);
}

.nav-toggle.active .bar:nth-child(1) {
    transform: rotate(45deg) translate(8px, 8px);
}

.nav-toggle.active .bar:nth-child(2) {
    opacity: 0;
}

.nav-toggle.active .bar:nth-child(3) {
    transform: rotate(-45deg) translate(7px, -7px);
}

/* Hero Section */
.hero {
    min-height: 100vh;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    padding: 4rem 6rem;
    padding-top: 8rem;
    position: relative;
}

.main-title {
    font-family: 'Black Ops One', cursive;
    font-size: clamp(8rem, 20vw, 16rem);
    font-weight: 400;
    color: var(--pink);
    text-transform: uppercase;
    letter-spacing: 0.1em;
    line-height: 0.9;
    text-shadow: 
        0 0 20px rgba(255, 182, 193, 0.5),
        0 0 40px rgba(255, 182, 193, 0.3),
        4px 4px 0px rgba(0, 0, 0, 0.8),
        8px 8px 0px rgba(0, 0, 0, 0.6);
    margin-top: 2rem;
    position: relative;
    z-index: 2;
}

.subtitle-section {
    display: flex;
    align-items: baseline;
    gap: 2rem;
    margin-top: -4rem;
    position: relative;
    z-index: 2;
}

.subtitle {
    font-family: 'Orbitron', sans-serif;
    font-size: clamp(1.5rem, 3vw, 2.5rem);
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.2em;
    color: var(--text-white);
}

.copyright {
    font-family: 'Rajdhani', sans-serif;
    font-size: clamp(0.875rem, 1.5vw, 1rem);
    font-weight: 400;
    color: var(--text-white);
    letter-spacing: 0.1em;
}

.hero-bottom {
    display: flex;
    justify-content: space-between;
    align-items: flex-end;
    margin-top: auto;
    padding-top: 4rem;
}

.hero-left {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}

.scroll-cta {
    font-family: 'Orbitron', sans-serif;
    font-size: clamp(1rem, 2vw, 1.5rem);
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.15em;
    color: var(--text-white);
}

.hero-description {
    font-family: 'Rajdhani', sans-serif;
    font-size: clamp(0.875rem, 1.5vw, 1rem);
    font-weight: 400;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    color: var(--text-white);
    max-width: 500px;
}

.hero-buttons {
    display: flex;
    gap: 1.5rem;
}

.btn {
    display: flex;
    align-items: center;
    gap: 1rem;
    padding: 1rem 2rem;
    background: var(--pink);
    border: none;
    color: var(--text-white);
    font-family: 'Orbitron', sans-serif;
    font-size: clamp(0.875rem, 1.2vw, 1rem);
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    cursor: pointer;
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.btn::before {
    content: '';
    position: absolute;
    left: 0;
    top: 0;
    width: 40px;
    height: 100%;
    background: rgba(0, 0, 0, 0.3);
    display: flex;
    align-items: center;
    justify-content: center;
}

.btn-icon {
    width: 40px;
    height: 40px;
    display: flex;
    align-items: center;
    justify-content: center;
    background: rgba(0, 0, 0, 0.3);
    font-size: 1.2rem;
    position: relative;
    z-index: 1;
}

.btn:hover {
    background: var(--pink-dark);
    transform: translateY(-2px);
    box-shadow: 0 10px 30px rgba(255, 182, 193, 0.4);
}

/* Sections */
.section {
    min-height: 100vh;
    padding: 8rem 6rem;
    position: relative;
}

.section-title {
    font-family: 'Black Ops One', cursive;
    font-size: clamp(4rem, 10vw, 8rem);
    color: var(--pink);
    text-transform: uppercase;
    letter-spacing: 0.1em;
    margin-bottom: 4rem;
    text-shadow: 
        0 0 20px rgba(255, 182, 193, 0.5),
        2px 2px 0px rgba(0, 0, 0, 0.8);
}

/* About Section */
.content-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 3rem;
    margin-top: 4rem;
}

.content-item {
    padding: 2rem;
    border: 1px solid rgba(255, 182, 193, 0.2);
    background: rgba(255, 182, 193, 0.05);
    transition: all 0.3s ease;
}

.content-item:hover {
    border-color: var(--pink);
    background: rgba(255, 182, 193, 0.1);
    transform: translateY(-5px);
}

.content-item h3 {
    font-family: 'Orbitron', sans-serif;
    font-size: 1.5rem;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    color: var(--pink);
    margin-bottom: 1rem;
}

.content-item p {
    font-family: 'Rajdhani', sans-serif;
    font-size: 1rem;
    line-height: 1.6;
    color: var(--text-gray);
}

/* Projects Section */
.projects-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 3rem;
    margin-top: 4rem;
}

.project-card {
    padding: 2.5rem;
    border: 1px solid rgba(255, 182, 193, 0.2);
    background: rgba(255, 182, 193, 0.05);
    position: relative;
    transition: all 0.3s ease;
    cursor: pointer;
    overflow: hidden;
}

.project-card::after {
    content: 'CLICK TO EXPAND â†’';
    position: absolute;
    bottom: 1rem;
    left: 2.5rem;
    font-family: 'Orbitron', sans-serif;
    font-size: 0.75rem;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    color: var(--pink);
    opacity: 0;
    transition: opacity 0.3s ease;
    pointer-events: none;
}

.project-card:hover {
