    background: rgba(255, 182, 193, 0.1);
    transform: translateY(-10px);
    box-shadow: 0 20px 40px rgba(255, 182, 193, 0.2);
}

.project-card:hover::after {
    opacity: 0.7;
}

.project-card.active {
    z-index: 100;
}

/* Card Expanded Overlay */
.card-expanded {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(10, 10, 10, 0.95);
    backdrop-filter: blur(20px);
    z-index: 2000;
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0;
    pointer-events: none;
    overflow-y: auto;
    padding: 2rem;
    will-change: transform, opacity, filter;
}

.card-expanded.active {
    opacity: 1;
    pointer-events: all;
}

.close-card {
    position: fixed;
    top: 2rem;
    right: 2rem;
    width: 60px;
    height: 60px;
    background: rgba(255, 182, 193, 0.1);
    border: 2px solid var(--pink);
    color: var(--pink);
    font-size: 2.5rem;
    font-family: 'Orbitron', sans-serif;
    font-weight: 700;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
    transition: all 0.3s ease;
    z-index: 2001;
