    font-size: 0.75rem;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.1em;
    color: var(--pink);
    opacity: 0;
    transition: opacity 0.3s ease;
    white-space: nowrap;
}

.close-card:hover::after {
