    padding-left: 2.5rem;
    position: relative;
    border-bottom: 1px solid rgba(255, 182, 193, 0.1);
}

.expanded-features li::before {
    content: 'â†’';
    position: absolute;
    left: 0;
    color: var(--pink);
    font-weight: 700;
}

.expanded-links {
