/* Blur effect for background when card is open */
body.card-open {
    overflow: hidden;
}

body.card-open .container {
    filter: blur(10px);
    pointer-events: none;
    transition: filter 0.3s ease;
}

/* Responsive for expanded cards */
@media (max-width: 768px) {
    .expanded-content {
        padding: 2.5rem;
        margin: 1rem;
