const startBtn = document.getElementById("startBtn");
const statusEl = document.getElementById("status");
const video = document.getElementById("video");
const canvas = document.getElementById("canvas");
const ctx = canvas.getContext("2d");
let stream;
let raf = 0;

function resize() {
  canvas.width = video.videoWidth || 640;
  canvas.height = video.videoHeight || 360;
}

function loop() {
  if (!video.videoWidth) {
    raf = requestAnimationFrame(loop);
    return;
  }
  resize();
  ctx.clearRect(0, 0, canvas.width, canvas.height);
  ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
  const data = ctx.getImageData(0, 0, canvas.width, canvas.height).data;
  let sum = 0;
  for (let i = 0; i < data.length; i += 40) {
    sum += data[i] + data[i + 1] + data[i + 2];
  }
  const brightness = sum / ((data.length / 40) * 3 * 255);
  const radius = 40 + brightness * 220;

  ctx.globalCompositeOperation = "screen";
  const g = ctx.createRadialGradient(canvas.width / 2, canvas.height / 2, 0, canvas.width / 2, canvas.height / 2, radius);
  g.addColorStop(0, "rgba(130,220,255,0.8)");
  g.addColorStop(1, "rgba(0,0,0,0)");
  ctx.fillStyle = g;
  ctx.beginPath();
  ctx.arc(canvas.width / 2, canvas.height / 2, radius, 0, Math.PI * 2);
  ctx.fill();
  ctx.globalCompositeOperation = "source-over";

  raf = requestAnimationFrame(loop);
}

startBtn.addEventListener("click", async () => {
  try {
    stream = await navigator.mediaDevices.getUserMedia({ video: true });
    video.srcObject = stream;
    statusEl.textContent = "Webcam reactive glow active.";
    cancelAnimationFrame(raf);
    raf = requestAnimationFrame(loop);
  } catch (error) {
    statusEl.textContent = "Webcam unavailable or permission denied.";
  }
});

window.addEventListener("beforeunload", () => {
  cancelAnimationFrame(raf);
  if (stream) stream.getTracks().forEach((t) => t.stop());
});

