  color: var(--text);
}

.page-header h1 em {
  font-style: normal;
  color: transparent;
  -webkit-text-stroke: 1px rgba(255,255,255,0.4);
}

.page-header p {
  font-size: 0.95rem;
  color: var(--muted);
  line-height: 1.8;
  max-width: 480px;
}

.page-header code {
  font-family: 'SF Mono', monospace;
  font-size: 0.8rem;
  color: #60c8ff;
  background: rgba(96, 200, 255, 0.1);
  padding: 0.1em 0.4em;
  border-radius: 3px;
}

/* â”€â”€ Gallery â”€â”€ */
.gallery-section { }

.gallery-label { margin-bottom: 1.5rem; }

.gallery-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-template-rows: auto;
  gap: 12px;
}

/* â”€â”€ Gallery cards â”€â”€ */
.gallery-card {
  position: relative;
  aspect-ratio: 3/4;
  border-radius: 4px;
  overflow: hidden;
  cursor: pointer;
  background: var(--panel);
  border: 1px solid var(--border);
  transition: transform 0.4s cubic-bezier(0.34, 1.56, 0.64, 1);
}

.gallery-card--wide {
  grid-column: span 2;
  aspect-ratio: 16/9;
}

.gallery-card:hover {
  transform: scale(1.02);
  z-index: 2;
}

/* Card art backgrounds */
.gc-art {
  position: absolute;
  inset: 0;
  transition: transform 0.5s ease;
}

.gallery-card:hover .gc-art {
  transform: scale(1.06);
}

.gc-art--1 {
  background:
    radial-gradient(ellipse at 40% 30%, rgba(160, 60, 255, 0.5) 0%, transparent 55%),
    radial-gradient(ellipse at 70% 70%, rgba(0, 180, 255, 0.3) 0%, transparent 45%),
    linear-gradient(160deg, #1a0a2e 0%, #0a0418 100%);
}
.gc-art--2 {
  background:
    radial-gradient(ellipse at 50% 60%, rgba(40, 200, 80, 0.4) 0%, transparent 60%),
    radial-gradient(ellipse at 80% 20%, rgba(20, 140, 60, 0.2) 0%, transparent 40%),
