/* â”€â”€ Lightbox â”€â”€ */
.lightbox {
  position: fixed;
  inset: 0;
  z-index: 500;
  background: #060810;
  display: grid;
  grid-template-columns: 1fr 380px;
  /* clip-path starts as an inset rectangle matching the clicked card */
  clip-path: inset(0 0 0 0 round 4px);
}

.lightbox[hidden] {
  display: none;
}

.lb-art {
  height: 100%;
  position: relative;
  overflow: hidden;
}

.lb-content {
  padding: 3rem 2.5rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
  border-left: 1px solid var(--border);
  overflow: auto;
}

.lb-meta {
  margin-bottom: 1.5rem;
}

.lb-title {
  font-family: 'Syne', sans-serif;
  font-size: 2.5rem;
  font-weight: 800;
  letter-spacing: -0.02em;
  color: var(--text);
  margin-bottom: 0.4rem;
  line-height: 1.1;
}

.lb-sub {
  font-size: 0.8rem;
  font-weight: 500;
  letter-spacing: 0.12em;
  text-transform: uppercase;
  color: var(--muted);
