.lb-close {
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1.5rem;
  background: transparent;
  border: 1px solid var(--border);
  color: var(--muted);
  font-family: 'Inter', sans-serif;
  font-size: 0.8rem;
  font-weight: 500;
  letter-spacing: 0.06em;
  cursor: pointer;
  border-radius: 2px;
  align-self: flex-start;
  transition: border-color 0.2s, color 0.2s;
}

.lb-close:hover {
  border-color: rgba(255,255,255,0.3);
  color: var(--text);
}

/* Nav buttons */
.lb-nav {
  position: absolute;
  bottom: 2rem;
  left: 2rem;
  display: flex;
  gap: 0.5rem;
}

.lb-prev, .lb-next {
  width: 44px;
  height: 44px;
  border: 1px solid var(--border);
  background: rgba(0,0,0,0.5);
  color: var(--text);
  font-size: 1rem;
  cursor: pointer;
  border-radius: 2px;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: border-color 0.2s, background 0.2s;
}

.lb-prev:hover, .lb-next:hover {
  border-color: rgba(255,255,255,0.4);
  background: rgba(255,255,255,0.05);
}

/* â”€â”€ Responsive â”€â”€ */
@media (max-width: 900px) {
  .gallery-grid { grid-template-columns: 1fr 1fr; }
  .gallery-card--wide { grid-column: span 2; }
  .lightbox { grid-template-columns: 1fr; }
  .lb-content { border-left: none; border-top: 1px solid var(--border); max-height: 40vh; }
}

@media (max-width: 600px) {
  .gallery-grid { grid-template-columns: 1fr; }
  .gallery-card--wide { grid-column: auto; aspect-ratio: 3/4; }
  .page { padding: 3rem 1.25rem 5rem; }
}

/* â”€â”€ Reduced motion â”€â”€ */
html.reduced-motion .gallery-card {
  transition: none;
}
html.reduced-motion .gc-art {
  transition: none;
}

