import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import { SplitText } from 'gsap/SplitText';
import Lenis from 'lenis';

gsap.registerPlugin(ScrollTrigger, SplitText);

initDemoShell({ title: 'Fitness Brand â€” Brutalist', category: 'pages', tech: ['gsap', 'splittext', 'scrolltrigger', 'lenis'] });

const lenis = new Lenis({ lerp: 0.1, smoothWheel: true });
lenis.on('scroll', ScrollTrigger.update);
gsap.ticker.add((time) => lenis.raf(time * 1000));
gsap.ticker.lagSmoothing(0);

const reduced = prefersReducedMotion();
if (reduced) document.documentElement.classList.add('reduced-motion');

// â”€â”€ Hero entrance â€” slammed in hard â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
if (!reduced) {
  gsap.set('.hero-tag', { opacity: 0, x: -30 });
  const heroLines = document.querySelectorAll('.hero-line');
  heroLines.forEach((line, i) => gsap.set(line, { opacity: 0, y: 80 + i * 20, skewY: 5 }));
  gsap.set('.hero-sub', { opacity: 0, y: 20 });
  gsap.set('.hero-ctas', { opacity: 0, y: 20 });

  const tl = gsap.timeline({ defaults: { ease: 'expo.out' } });
  tl.to('.hero-tag', { opacity: 1, x: 0, duration: 0.6, delay: 0.3 })
    .to(heroLines, { opacity: 1, y: 0, skewY: 0, duration: 0.7, stagger: 0.07 }, '-=0.3')
    .to('.hero-sub', { opacity: 1, y: 0, duration: 0.6 }, '-=0.3')
    .to('.hero-ctas', { opacity: 1, y: 0, duration: 0.5 }, '-=0.2');
}

// â”€â”€ Stats Bar â€” scroll counters â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
document.querySelectorAll('.stat-big').forEach((el) => {
  const target = parseFloat(el.dataset.target);
  const suffix = el.dataset.suffix || '';

  if (!reduced) {
    ScrollTrigger.create({
      trigger: '.stats-bar',
      start: 'top 80%',
      end: 'top 20%',
      onUpdate: (self) => {
        const val = target * self.progress;
        el.textContent = (target % 1 !== 0 ? val.toFixed(0) : Math.round(val)) + suffix;
      },
    });

    gsap.set(el, { opacity: 0, y: 30 });
    gsap.to(el, {
      opacity: 1, y: 0, duration: 0.8, ease: 'expo.out',
      scrollTrigger: { trigger: '.stats-bar', start: 'top 80%', toggleActions: 'play none none reverse' },
    });
  } else {
    el.textContent = target + suffix;
  }
});

// â”€â”€ Programs â€” hard entrance â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
if (!reduced) {
  const hSplit = new SplitText('.programs-header h2', { type: 'lines' });
  gsap.set(hSplit.lines, { opacity: 0, y: 50 });
  gsap.to(hSplit.lines, {
    opacity: 1, y: 0, duration: 0.8, stagger: 0.1, ease: 'expo.out',
    scrollTrigger: { trigger: '.programs-header', start: 'top 70%', toggleActions: 'play none none reverse' },
  });

  document.querySelectorAll('.program-card').forEach((card, i) => {
    gsap.set(card, { opacity: 0, y: 60 });
    gsap.to(card, {
      opacity: 1, y: 0, duration: 0.7, ease: 'expo.out', delay: i * 0.1,
      scrollTrigger: { trigger: '.programs-grid', start: 'top 75%', toggleActions: 'play none none reverse' },
    });
  });
}

// â”€â”€ Transform section â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
if (!reduced) {
  const tSplit = new SplitText('.transform-text h2', { type: 'lines' });
  gsap.set(tSplit.lines, { opacity: 0, x: -40 });
  gsap.to(tSplit.lines, {
    opacity: 1, x: 0, duration: 0.8, stagger: 0.1, ease: 'expo.out',
    scrollTrigger: { trigger: '.transform-section', start: 'top 65%', toggleActions: 'play none none reverse' },
  });

  gsap.set('.result-list li', { opacity: 0, x: -20 });
  gsap.to('.result-list li', {
    opacity: 1, x: 0, duration: 0.5, stagger: 0.1, ease: 'expo.out',
    scrollTrigger: { trigger: '.result-list', start: 'top 75%', toggleActions: 'play none none reverse' },
  });

  gsap.set(['.tv-box--before', '.tv-arrow', '.tv-box--after'], { opacity: 0, scale: 0.85 });
  gsap.to(['.tv-box--before', '.tv-arrow', '.tv-box--after'], {
    opacity: 1, scale: 1, duration: 0.7, stagger: 0.15, ease: 'back.out(1.4)',
    scrollTrigger: { trigger: '.transform-visual', start: 'top 75%', toggleActions: 'play none none reverse' },
  });
}

// â”€â”€ Testimonials â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
if (!reduced) {
  document.querySelectorAll('.testi-card').forEach((card, i) => {
    gsap.set(card, { opacity: 0, y: 40 });
    gsap.to(card, {
      opacity: 1, y: 0, duration: 0.7, delay: i * 0.1, ease: 'expo.out',
      scrollTrigger: { trigger: '.testi-grid', start: 'top 75%', toggleActions: 'play none none reverse' },
    });
  });
}

// â”€â”€ CTA join â€” big slam â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
if (!reduced) {
  const jSplit = new SplitText('.join-inner h2', { type: 'lines' });
  gsap.set(jSplit.lines, { opacity: 0, y: 80 });
  gsap.to(jSplit.lines, {
    opacity: 1, y: 0, duration: 1, stagger: 0.1, ease: 'expo.out',
    scrollTrigger: { trigger: '.join-section', start: 'top 70%', toggleActions: 'play none none reverse' },
  });

  gsap.set('.join-inner p, .btn-giant', { opacity: 0, y: 25 });
  gsap.to('.join-inner p, .btn-giant', {
    opacity: 1, y: 0, duration: 0.7, stagger: 0.15, ease: 'expo.out',
    scrollTrigger: { trigger: '.join-section', start: 'top 60%', toggleActions: 'play none none reverse' },
  });
}

// â”€â”€ Motion preference â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
window.addEventListener('motion-preference', (e) => {
  gsap.globalTimeline.paused(e.detail.reduced);
});

snippets.css:
:root {
  --bg: #070a12;
  --text: #f0f4fb;
  --panel: #121a2b;
  --border: #263249;
  --accent: #86e8ff;
  --muted: #8a95a8;
  --purple: #ae52ff;
  --warm: #ffcc66;
  --pink: #ff40d6;
  --hue-shift: 0deg;
}

* { margin: 0; padding: 0; box-sizing: border-box; }

body {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  background: var(--bg);
  color: var(--text);
  line-height: 1.6;
  overflow-x: hidden;
}

/* â”€â”€ Hero â”€â”€ */
.hero {
  position: relative;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 5rem 2rem 3rem;
  overflow: hidden;
}

.hero-bg {
  position: absolute;
  inset: 0;
  background: radial-gradient(ellipse at 50% 30%, rgba(134, 232, 255, 0.08) 0%, transparent 65%),
              radial-gradient(ellipse at 70% 70%, rgba(174, 82, 255, 0.06) 0%, transparent 60%);
  pointer-events: none;
}

.hero-content {
  position: relative;
  max-width: 680px;
  text-align: center;
  z-index: 1;
}

.hero-badge {
  display: inline-block;
  font-size: 0.7rem;
  font-weight: 700;
  letter-spacing: 2px;
  text-transform: uppercase;
  color: var(--accent);
  border: 1px solid rgba(134, 232, 255, 0.3);
  border-radius: 20px;
  padding: 0.3rem 1rem;
  margin-bottom: 1.5rem;
}

.hero-title {
  font-size: 4rem;
  font-weight: 900;
  line-height: 1.05;
  margin-bottom: 1.5rem;
  letter-spacing: -2px;
}

.hero-sub {
  font-size: 1.15rem;
  color: var(--muted);
  max-width: 500px;
  margin: 0 auto 2rem;
  line-height: 1.8;
}

.hero-cta {
  display: flex;
  gap: 1rem;
  justify-content: center;
  flex-wrap: wrap;
}

.btn-primary {
  display: inline-block;
  padding: 0.85rem 1.8rem;
  background: var(--accent);
  color: var(--bg);
  border-radius: 6px;
  font-weight: 700;
  text-decoration: none;
  transition: all 0.3s ease;
}

.btn-primary:hover {
  background: rgba(134, 232, 255, 0.85);
  transform: translateY(-2px);
}

.btn-ghost {
  display: inline-block;
  padding: 0.85rem 1.8rem;
  border: 1px solid var(--border);
  color: var(--muted);
  border-radius: 6px;
  font-weight: 600;
  text-decoration: none;
  transition: all 0.3s ease;
}

.btn-ghost:hover {
  border-color: var(--accent);
  color: var(--accent);
  transform: translateY(-2px);
}

.hero-scroll-hint {
  position: absolute;
  bottom: 2.5rem;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.5rem;
  color: var(--muted);
  font-size: 0.7rem;
  letter-spacing: 1px;
  text-transform: uppercase;
}

.scroll-arrow {
  width: 1px;
  height: 30px;
  background: linear-gradient(to bottom, var(--muted), transparent);
  animation: scrollBounce 2s ease-in-out infinite;
}

@keyframes scrollBounce {
  0%, 100% { transform: scaleY(1); opacity: 0.5; }
  50% { transform: scaleY(1.3); opacity: 1; }
}

/* â”€â”€ Gradient Text â”€â”€ */
.gradient-text {
  background: linear-gradient(
    90deg,
    hsl(var(--hue-shift), 80%, 55%),
    hsl(calc(var(--hue-shift) + 60deg), 80%, 55%),
    hsl(calc(var(--hue-shift) + 120deg), 80%, 55%)
  );
  background-size: 200% 100%;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  animation: gradShift 8s linear infinite;
}

@keyframes gradShift {
  0% { background-position: 0% center; }
  100% { background-position: 200% center; }
}

@supports not (-webkit-background-clip: text) {
  .gradient-text { color: var(--accent); background: none; }
}

/* â”€â”€ Section Shared â”€â”€ */
.section-header {
  text-align: center;
  max-width: 600px;
  margin: 0 auto 4rem;
}

.section-tag {
  display: inline-block;
  font-size: 0.7rem;
  font-weight: 700;
  letter-spacing: 2px;
  text-transform: uppercase;
  color: var(--accent);
  margin-bottom: 1rem;
}

.section-title {
  font-size: 2.5rem;
  font-weight: 800;
  margin-bottom: 1rem;
  letter-spacing: -0.5px;
}

.section-desc {
  font-size: 1rem;
  color: var(--muted);
  line-height: 1.8;
}

/* â”€â”€ Metrics â”€â”€ */
.metrics-section {
  padding: 7rem 2rem;
  border-top: 1px solid var(--border);
}

.metrics-grid {
  max-width: 1100px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 1.5rem;
}

.metric-card {
  background: var(--panel);
  border: 1px solid var(--border);
  border-radius: 10px;
  padding: 1.8rem 1.5rem;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
}

.metric-card::before {
  content: '';
  position: absolute;
  inset: 0;
  background: linear-gradient(135deg, rgba(134, 232, 255, 0.04) 0%, transparent 60%);
  pointer-events: none;
}

.metric-card:hover {
  border-color: var(--accent);
  background: rgba(134, 232, 255, 0.06);
  transform: translateY(-3px);
}

.metric-icon {
  font-size: 1.2rem;
  color: var(--accent);
  margin-bottom: 1rem;
  opacity: 0.7;
}

.metric-value {
  font-size: 2.2rem;
  font-weight: 800;
  color: var(--accent);
  letter-spacing: -1px;
  margin-bottom: 0.4rem;
  font-variant-numeric: tabular-nums;
}

.metric-label {
  font-size: 0.9rem;
  font-weight: 600;
  color: var(--text);
  margin-bottom: 0.3rem;
}

.metric-sub {
  font-size: 0.78rem;
  color: var(--muted);
  margin-bottom: 1rem;
}

.metric-bar {
  height: 3px;
  background: var(--border);
  border-radius: 2px;
  overflow: hidden;
}

.metric-bar-fill {
  height: 100%;
  width: var(--bar-w, 50%);
  background: linear-gradient(to right, var(--accent), var(--purple));
  border-radius: 2px;
  transform-origin: left center;
}

/* â”€â”€ Charts â”€â”€ */
.charts-section {
  padding: 7rem 2rem;
  border-top: 1px solid var(--border);
  background: rgba(18, 26, 43, 0.5);
}

.charts-grid {
  max-width: 1100px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 1fr;
  grid-template-rows: auto auto;
  gap: 1.5rem;
}

.chart-card {
  background: var(--panel);
  border: 1px solid var(--border);
  border-radius: 10px;
  padding: 1.5rem;
  transition: border-color 0.3s ease;
}

.chart-card:hover {
  border-color: rgba(134, 232, 255, 0.3);
}

.chart-wide {
  grid-column: 1 / -1;
}

.chart-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 1.2rem;
}

.chart-header h3 {
  font-size: 0.95rem;
  font-weight: 600;
  color: var(--text);
}

.chart-badge {
  font-size: 0.7rem;
  font-weight: 700;
  padding: 0.25rem 0.6rem;
  border-radius: 20px;
  background: rgba(134, 232, 255, 0.1);
  color: var(--muted);
  border: 1px solid var(--border);
}

.trend-up { color: #4ade80; background: rgba(74, 222, 128, 0.1); border-color: rgba(74, 222, 128, 0.3); }

canvas {
  display: block;
  width: 100%;
  height: auto;
}

.chart-legend {
  display: flex;
  gap: 1.5rem;
  margin-top: 1rem;
}

.legend-item {
  display: flex;
  align-items: center;
  gap: 0.4rem;
  font-size: 0.8rem;
  color: var(--muted);
}

.dot {
  width: 8px;
  height: 8px;
  border-radius: 50%;
}

.dot.accent { background: rgba(134, 232, 255, 0.5); }
.dot.purple { background: var(--purple); }

.donut-legend {
  margin-top: 1rem;
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.dl-item {
  display: flex;
  align-items: center;
  gap: 0.6rem;
  font-size: 0.82rem;
  color: var(--muted);
}

.dl-dot {
  width: 8px;
  height: 8px;
  border-radius: 50%;
  flex-shrink: 0;
}

.dl-item span:nth-child(2) { flex: 1; }
.dl-item strong { color: var(--text); }

/* â”€â”€ Timeline â”€â”€ */
.timeline-section {
  padding: 7rem 2rem;
  border-top: 1px solid var(--border);
}

.timeline {
  max-width: 700px;
  margin: 0 auto;
  position: relative;
}

.timeline::before {
  content: '';
  position: absolute;
  left: 50%;
  top: 0;
  bottom: 0;
  width: 1px;
  background: var(--border);
  transform: translateX(-50%);
}

.tl-item {
  display: flex;
  align-items: flex-start;
  margin-bottom: 3rem;
  position: relative;
}

.tl-item[data-side="left"] { flex-direction: row; }
.tl-item[data-side="right"] { flex-direction: row-reverse; }

.tl-dot {
  position: absolute;
  left: 50%;
  top: 1.2rem;
  transform: translateX(-50%);
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background: var(--border);
  border: 2px solid var(--panel);
  z-index: 1;
}

.tl-dot--active {
  background: var(--accent);
  box-shadow: 0 0 12px rgba(134, 232, 255, 0.5);
}

.tl-card {
  width: calc(50% - 2rem);
  background: var(--panel);
  border: 1px solid var(--border);
  border-radius: 8px;
  padding: 1.2rem 1.5rem;
}

.tl-card--active {
  border-color: var(--accent);
  background: rgba(134, 232, 255, 0.06);
}

.tl-item[data-side="left"] .tl-card { margin-right: auto; }
.tl-item[data-side="right"] .tl-card { margin-left: auto; }

.tl-date {
  font-size: 0.7rem;
  font-weight: 700;
  letter-spacing: 1.5px;
  text-transform: uppercase;
  color: var(--accent);
  display: block;
  margin-bottom: 0.4rem;
}

.tl-card h4 {
  font-size: 1rem;
  font-weight: 700;
  margin-bottom: 0.5rem;
  color: var(--text);
}

.tl-card p {
  font-size: 0.85rem;
  color: var(--muted);
  line-height: 1.7;
}

/* â”€â”€ Team â”€â”€ */
.team-section {
  padding: 7rem 2rem;
  border-top: 1px solid var(--border);
  background: rgba(18, 26, 43, 0.5);
}

.team-grid {
  max-width: 800px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(140px, 1fr));
  gap: 1.5rem;
}

.team-card {
  text-align: center;
  padding: 1.5rem 1rem;
  background: var(--panel);
  border: 1px solid var(--border);
  border-radius: 10px;
  transition: all 0.3s ease;
}

.team-card:hover {
  border-color: var(--accent);
  transform: translateY(-4px);
}

.team-avatar {
  width: 56px;
  height: 56px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto 1rem;
  font-size: 0.85rem;
  font-weight: 800;
  letter-spacing: 1px;
  background: hsl(var(--av-hue), 70%, 15%);
  border: 2px solid hsl(var(--av-hue), 70%, 35%);
  color: hsl(var(--av-hue), 70%, 65%);
}

.team-card h4 {
  font-size: 0.92rem;
  font-weight: 700;
  color: var(--text);
  margin-bottom: 0.3rem;
}

.team-card span {
  font-size: 0.78rem;
  color: var(--muted);
}

/* â”€â”€ CTA â”€â”€ */
.cta-section {
  padding: 8rem 2rem;
  text-align: center;
  border-top: 1px solid var(--border);
  background: radial-gradient(ellipse at 50% 50%, rgba(134, 232, 255, 0.06) 0%, transparent 70%);
}

.cta-section h2 {
  font-size: 3rem;
  font-weight: 900;
  margin-bottom: 1rem;
  letter-spacing: -1px;
}

.cta-section p {
  font-size: 1.1rem;
  color: var(--muted);
  margin-bottom: 2rem;
}

/* â”€â”€ Responsive â”€â”€ */
@media (max-width: 900px) {
  .charts-grid {
    grid-template-columns: 1fr;
  }
  .chart-wide { grid-column: 1; }
}

@media (max-width: 768px) {
  .hero-title { font-size: 2.8rem; }
  .section-title { font-size: 2rem; }
  .metrics-grid { grid-template-columns: repeat(2, 1fr); }

  .timeline::before { left: 1.2rem; }
  .tl-item, .tl-item[data-side="right"] { flex-direction: row; }
  .tl-dot { left: 1.2rem; }
  .tl-card { width: calc(100% - 4rem); margin-left: 3rem !important; margin-right: 0 !important; }

  .team-grid { grid-template-columns: repeat(3, 1fr); }
}

@media (max-width: 480px) {
  .hero-title { font-size: 2.2rem; }
  .metrics-grid { grid-template-columns: 1fr; }
  .team-grid { grid-template-columns: repeat(2, 1fr); }
  .cta-section h2 { font-size: 2rem; }
}

html.reduced-motion .gradient-text { animation: none !important; }
html.reduced-motion * {
  animation-duration: 0.01ms !important;
  transition-duration: 0.01ms !important;
}

