// â”€â”€â”€ Motion preference â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
window.addEventListener('motion-preference', (e) => {
  if (e.detail.reduced) {
    gsap.globalTimeline.paused(true);
    animateBarChart(barCanvas, 1);
    animateDonutChart(donutCanvas, 1);
    animateAreaChart(areaCanvas, 1);
  } else {
    gsap.globalTimeline.paused(false);
  }
});

