if (!window.MotionPreference) {
  const __mql = window.matchMedia("(prefers-reduced-motion: reduce)");
  const __listeners = new Set();

  const MotionPreference = {
    prefersReducedMotion() {
      return __mql.matches;
    },
    setOverride(value) {
      const reduced = Boolean(value);
      document.documentElement.classList.toggle("reduced-motion", reduced);
      window.dispatchEvent(new CustomEvent("motion-preference", { detail: { reduced } }));
      for (const listener of __listeners) {
        try {
          listener({ reduced, override: reduced, systemReduced: __mql.matches });
        } catch {}
      }
    },
    onChange(listener) {
      __listeners.add(listener);
      try {
        listener({
          reduced: __mql.matches,
          override: null,
          systemReduced: __mql.matches,
        });
      } catch {}
      return () => __listeners.delete(listener);
    },
    getState() {
      return { reduced: __mql.matches, override: null, systemReduced: __mql.matches };
    },
  };

  window.MotionPreference = MotionPreference;
}

function prefersReducedMotion() {
  return window.MotionPreference.prefersReducedMotion();
}

function initDemoShell() {
  // No-op shim in imported standalone snippets.
}

import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import { SplitText } from 'gsap/SplitText';
import Lenis from 'lenis';

gsap.registerPlugin(ScrollTrigger, SplitText);

initDemoShell({
  title: 'SaaS Landing Page',
  category: 'pages',
  tech: ['gsap', 'lenis', 'scrolltrigger', 'splittext'],
});

const lenis = new Lenis({ lerp: 0.1, smoothWheel: true });
lenis.on('scroll', ScrollTrigger.update);
gsap.ticker.add((time) => lenis.raf(time * 1000));
gsap.ticker.lagSmoothing(0);

let reduced = prefersReducedMotion();
if (reduced) document.documentElement.classList.add('reduced-motion');

window.addEventListener('motion-preference', (e) => {
  reduced = e.detail.reduced;
  document.documentElement.classList.toggle('reduced-motion', reduced);
  ScrollTrigger.refresh();
});

const dur = (d) => reduced ? 0 : d;

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// HERO ENTRANCE
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const heroTitle = document.querySelector('.hero-title');
const heroSubtitle = document.querySelector('.hero-subtitle');
const heroButtons = document.querySelectorAll('.hero-buttons .btn');
const heroVisual = document.querySelector('.hero-visual');

// SplitText word-by-word reveal for headline
const titleSplit = new SplitText(heroTitle, { type: 'words', wordsClass: 'word' });
gsap.set(titleSplit.words, {
  opacity: 0,
  y: reduced ? 0 : 30,
});

const heroTl = gsap.timeline({ delay: 0.4 });

heroTl
  .to(titleSplit.words, {
    opacity: 1,
    y: 0,
    duration: dur(0.7),
    ease: 'expo.out',
    stagger: { each: 0.06 },
  })
  .to(heroSubtitle, {
    opacity: 1,
    y: 0,
    duration: dur(0.6),
    ease: 'expo.out',
  }, '-=0.3')
  .to(heroButtons, {
    opacity: 1,
    y: 0,
    duration: dur(0.5),
    ease: 'back.out(1.7)',
    stagger: { each: 0.1 },
  }, '-=0.3')
  .fromTo(heroVisual, {
    opacity: 0,
    y: reduced ? 0 : 40,
    scale: reduced ? 1 : 0.95,
  }, {
    opacity: 1,
    y: 0,
    scale: 1,
    duration: dur(0.8),
    ease: 'expo.out',
  }, '-=0.4');

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// FEATURES: Pinned section with content + device swap
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const featureCards = document.querySelectorAll('.feature-card');
const featureDevices = document.querySelectorAll('.feature-device');
const stepDots = document.querySelectorAll('.step-dot');

let activeFeature = 0;

// First card and device already have .active in HTML

function setActiveFeature(index) {
  if (index === activeFeature) return;

  // Cards
  featureCards.forEach((c) => c.classList.remove('active'));
  featureCards[index].classList.add('active');

  // Devices
  featureDevices.forEach((d) => d.classList.remove('active'));
  featureDevices[index].classList.add('active');

  // Dots
  stepDots.forEach((d) => d.classList.remove('active'));
  stepDots[index].classList.add('active');

  activeFeature = index;
}

ScrollTrigger.create({
  trigger: '.feature-track',
  start: 'top top',
  end: 'bottom bottom',
  scrub: 0,
  onUpdate: (self) => {
    const p = self.progress;
    if (p < 0.33) setActiveFeature(0);
    else if (p < 0.66) setActiveFeature(1);
    else setActiveFeature(2);
  },
});

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// PRICING: Heading + card stagger
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const pricingHeading = document.querySelector('.pricing-heading');
if (pricingHeading) {
  const pricingSplit = new SplitText(pricingHeading, { type: 'words', wordsClass: 'word' });
  gsap.set(pricingSplit.words, { opacity: 0, y: reduced ? 0 : 25 });

  gsap.to(pricingSplit.words, {
    opacity: 1,
    y: 0,
    duration: dur(0.5),
    ease: 'expo.out',
    stagger: { each: 0.04 },
    scrollTrigger: {
      trigger: '.pricing-section',
      start: 'top 75%',
      toggleActions: 'play none none reverse',
    },
  });
}

const pricingCards = document.querySelectorAll('.pricing-card');
gsap.set(pricingCards, {
  opacity: 0,
  y: reduced ? 0 : 40,
});

gsap.to(pricingCards, {
  opacity: 1,
  y: 0,
  duration: dur(0.6),
  ease: 'expo.out',
  stagger: {
    each: 0.1,
    from: 'center',
  },
  scrollTrigger: {
    trigger: '.pricing-grid',
    start: 'top 80%',
    toggleActions: 'play none none reverse',
  },
  onComplete: () => {
    // Ensure popular card retains its scale after animation
    const popular = document.querySelector('.pricing-card.popular');
    if (popular) {
      gsap.set(popular, { scale: 1.02 });
    }
  },
});

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// TESTIMONIALS: Heading + card stagger with depth
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const testimonialsHeading = document.querySelector('.testimonials-heading');
if (testimonialsHeading) {
  const testimonialsSplit = new SplitText(testimonialsHeading, { type: 'words', wordsClass: 'word' });
  gsap.set(testimonialsSplit.words, { opacity: 0, y: reduced ? 0 : 25 });

  gsap.to(testimonialsSplit.words, {
    opacity: 1,
    y: 0,
    duration: dur(0.5),
    ease: 'expo.out',
    stagger: { each: 0.04 },
    scrollTrigger: {
      trigger: '.testimonials-section',
      start: 'top 75%',
      toggleActions: 'play none none reverse',
    },
  });
}

const testimonialCards = document.querySelectorAll('.testimonial-card');
gsap.set(testimonialCards, {
  opacity: 0,
  y: reduced ? 0 : 30,
  rotateX: reduced ? 0 : -5,
});

gsap.to(testimonialCards, {
  opacity: 1,
  y: 0,
  rotateX: 0,
  duration: dur(0.6),
  ease: 'expo.out',
  stagger: { each: 0.12 },
  scrollTrigger: {
    trigger: '.testimonials-grid',
    start: 'top 80%',
    toggleActions: 'play none none reverse',
  },
});

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// CTA: Char-by-char SplitText + subtitle + button
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const ctaTitle = document.querySelector('.cta-title');
const ctaSubtitle = document.querySelector('.cta-subtitle');
const ctaBtn = document.querySelector('.cta-section .btn');

if (ctaTitle) {
  const ctaSplit = new SplitText(ctaTitle, { type: 'chars', charsClass: 'char' });
  gsap.set(ctaSplit.chars, {
    opacity: 0,
    y: reduced ? 0 : 20,
  });

  const ctaTl = gsap.timeline({
    scrollTrigger: {
      trigger: '.cta-section',
      start: 'top 65%',
      toggleActions: 'play none none reverse',
    },
  });

  ctaTl
    .to(ctaSplit.chars, {
      opacity: 1,
      y: 0,
      duration: dur(0.4),
      ease: 'expo.out',
      stagger: { each: 0.02 },
    })
    .to(ctaSubtitle, {
      opacity: 1,
      y: 0,
      duration: dur(0.6),
      ease: 'expo.out',
    }, '-=0.2')
    .to(ctaBtn, {
      opacity: 1,
      y: 0,
      duration: dur(0.5),
      ease: 'back.out(1.7)',
    }, '-=0.3');
}

snippets.css:
:root {
  color-scheme: dark;
  --bg: #06070c;
  --panel: rgba(12, 14, 22, 0.82);
  --stroke: rgba(255, 255, 255, 0.1);
  --text: #f5f2ef;
  --muted: rgba(245, 242, 239, 0.6);
  --red: #ff2e3a;
  --red-soft: rgba(255, 46, 58, 0.2);
  --blue: #6dd5ff;
}

* {
  box-sizing: border-box;
}

body {
  margin: 0;
  min-height: 100vh;
  font-family: "Space Grotesk", sans-serif;
  background: radial-gradient(circle at 10% 10%, rgba(255, 46, 58, 0.15), transparent 55%),
    radial-gradient(circle at 80% 20%, rgba(109, 213, 255, 0.15), transparent 60%),
    #05060a;
  color: var(--text);
  overflow-x: hidden;
}

a {
  color: inherit;
  text-decoration: none;
}

#bg {
  position: fixed;
  inset: 0;
  z-index: 0;
}

.speed-lines {
  position: fixed;
  inset: 0;
  pointer-events: none;
  z-index: 1;
  background: repeating-linear-gradient(
      100deg,
      rgba(255, 255, 255, 0.06) 0,
      rgba(255, 255, 255, 0.06) 1px,
      transparent 1px,
      transparent 18px
    );
  opacity: 0.25;
  transform: translateX(var(--speed-shift, 0px));
  transition: opacity 0.4s ease;
}

.site-header {
  position: sticky;
  top: 0;
  z-index: 5;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 24px clamp(20px, 6vw, 80px);
  backdrop-filter: blur(14px);
  background: rgba(5, 6, 10, 0.85);
  border-bottom: 1px solid rgba(255, 255, 255, 0.08);
}

.logo {
  display: flex;
  align-items: center;
  gap: 16px;
}

.logo-mark {
  width: 46px;
  height: 46px;
  border-radius: 14px;
  background: linear-gradient(140deg, var(--red), #ff7a45);
  box-shadow: 0 0 26px rgba(255, 46, 58, 0.45);
}

.logo-title {
  font-family: "Bebas Neue", sans-serif;
  font-size: 1.4rem;
  letter-spacing: 0.1em;
  margin: 0;
}

.logo-sub {
  margin: 4px 0 0;
  color: var(--muted);
  font-size: 0.85rem;
}

.nav {
  display: flex;
  align-items: center;
  gap: 20px;
  font-size: 0.85rem;
  text-transform: uppercase;
  letter-spacing: 0.15em;
}

.nav a {
  color: var(--muted);
}

.nav-cta {
  padding: 0.5rem 1.2rem;
  border-radius: 999px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  color: var(--text);
  background: rgba(255, 46, 58, 0.15);
}

.page {
  position: relative;
  z-index: 2;
  padding: 0 clamp(20px, 6vw, 80px) 80px;
}

.hero {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 36px;
  padding: 64px 0 40px;
}

.eyebrow {
  text-transform: uppercase;
  letter-spacing: 0.3em;
  color: var(--blue);
  font-size: 0.7rem;
}

.hero-copy h1 {
  font-family: "Bebas Neue", sans-serif;
  font-size: clamp(2.8rem, 5vw, 4.5rem);
  margin: 16px 0;
}

.lead {
  color: var(--muted);
  line-height: 1.6;
  max-width: 60ch;
}

.hero-actions {
  margin-top: 24px;
  display: flex;
  gap: 12px;
  flex-wrap: wrap;
}

.btn {
  padding: 0.85rem 1.6rem;
  border-radius: 999px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  background: transparent;
  color: var(--text);
  text-transform: uppercase;
  letter-spacing: 0.2em;
  font-size: 0.65rem;
}

.btn.primary {
  background: rgba(255, 46, 58, 0.25);
  border-color: rgba(255, 46, 58, 0.6);
}

.btn.ghost {
  border-color: rgba(109, 213, 255, 0.6);
}

.hero-metrics {
  margin-top: 24px;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(140px, 1fr));
  gap: 12px;
}

.hero-metrics div {
  background: var(--panel);
  border: 1px solid var(--stroke);
  border-radius: 14px;
  padding: 14px;
}

.hero-metrics span {
  display: block;
  color: var(--muted);
  font-size: 0.8rem;
}

.hero-panel {
  display: grid;
  gap: 18px;
}

.panel-card {
  background: var(--panel);
  border: 1px solid var(--stroke);
  border-radius: 18px;
  padding: 20px;
}

.panel-title {
  text-transform: uppercase;
  letter-spacing: 0.2em;
  font-size: 0.65rem;
  color: var(--muted);
}

.marquee {
  margin: 24px 0 0;
  overflow: hidden;
  border-top: 1px solid rgba(255, 255, 255, 0.08);
  border-bottom: 1px solid rgba(255, 255, 255, 0.08);
}

.marquee-track {
  display: flex;
  gap: 48px;
  padding: 14px 0;
  text-transform: uppercase;
  letter-spacing: 0.35em;
  font-size: 0.7rem;
  color: var(--muted);
  white-space: nowrap;
  will-change: transform;
}

.section {
  padding: 56px 0;
}

.section-header {
  display: flex;
  justify-content: space-between;
  align-items: baseline;
  gap: 16px;
}

.section-header p {
  color: var(--muted);
}

.grid {
  margin-top: 24px;
  display: grid;
  gap: 20px;
  grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
}

.card {
  background: var(--panel);
  border: 1px solid var(--stroke);
  border-radius: 16px;
  padding: 20px;
}

.card span {
  text-transform: uppercase;
  letter-spacing: 0.2em;
  font-size: 0.65rem;
  color: var(--blue);
}

.card p {
  color: var(--muted);
}

.split {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
  gap: 24px;
}

.list {
  list-style: none;
  padding: 0;
  margin: 18px 0 0;
  display: grid;
  gap: 12px;
}

.list li {
  padding: 12px 16px;
  border-radius: 12px;
  background: rgba(12, 14, 20, 0.8);
  border: 1px solid var(--stroke);
}

.pill-grid {
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-top: 18px;
}

.pill-grid span {
  padding: 8px 14px;
  border-radius: 999px;
  border: 1px solid rgba(109, 213, 255, 0.5);
  font-size: 0.75rem;
}

.stats {
  display: grid;
  gap: 20px;
  grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
}

.stats div {
  background: var(--panel);
  border: 1px solid var(--stroke);
  border-radius: 16px;
  padding: 20px;
  text-align: center;
}

.stats h2 {
  font-family: "Bebas Neue", sans-serif;
  font-size: 2.4rem;
  margin: 0 0 8px;
  color: var(--red);
}

.lab-grid {
  display: grid;
  gap: 16px;
  grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
}

.lab-card {
  padding: 18px;
  border-radius: 16px;
  border: 1px solid rgba(255, 255, 255, 0.12);
  background: rgba(8, 10, 16, 0.8);
}

.contact-cards {
  display: grid;
  gap: 16px;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
}

.contact-card {
  padding: 18px;
  border-radius: 14px;
  background: rgba(12, 14, 20, 0.85);
  border: 1px solid rgba(255, 255, 255, 0.12);
}

.contact-card p {
  margin: 6px 0 0;
  color: var(--muted);
}

@media (max-width: 900px) {
  .site-header {
    flex-direction: column;
    align-items: flex-start;
  }

  .nav {
    flex-wrap: wrap;
  }
}

@media (max-width: 640px) {
  .page {
    padding: 0 18px 80px;
  }
}

