:root {
  --black: #080810;
  --dark: #0d0d1a;
  --panel: #111120;
  --border: #1e1e38;
  --text: #e8e8f8;
  --muted: #6b6b90;
  --faint: #2a2a48;

  --magenta: #ff2d78;
  --cyan: #00e5ff;
  --lime: #a8ff3e;
  --purple: #9b4dff;
  --gold: #ffcc00;

  --neon-glow-m: 0 0 8px rgba(255, 45, 120, 0.6), 0 0 25px rgba(255, 45, 120, 0.25);
  --neon-glow-c: 0 0 8px rgba(0, 229, 255, 0.6), 0 0 25px rgba(0, 229, 255, 0.25);
}

*, *::before, *::after {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
