import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import Lenis from 'lenis';

gsap.registerPlugin(ScrollTrigger);

initDemoShell({
  title: 'FREQ â€” Music Platform',
  category: 'pages',
  tech: ['gsap', 'scroll-trigger', 'lenis', 'canvas-2d', 'space-mono'],
});

const reduced = prefersReducedMotion();
if (reduced) document.documentElement.classList.add('reduced-motion');

// â”€â”€ Lenis smooth scroll â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const lenis = new Lenis({ lerp: 0.1, smoothWheel: true });
lenis.on('scroll', ScrollTrigger.update);
gsap.ticker.add((time) => lenis.raf(time * 1000));
gsap.ticker.lagSmoothing(0);

// â”€â”€ Canvas Waveform Background â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const canvas = document.getElementById('waveform-canvas');
const ctx = canvas.getContext('2d');
let w, h;
let scrollY = 0;

function resizeCanvas() {
  w = canvas.width = window.innerWidth;
  h = canvas.height = window.innerHeight;
}
resizeCanvas();
window.addEventListener('resize', resizeCanvas);

lenis.on('scroll', (e) => { scrollY = e.scroll; });

function drawWaveform(time) {
  ctx.clearRect(0, 0, w, h);

  const numWaves = 4;
  const waveConfigs = [
    { color: 'rgba(255,45,120,', freq: 0.003, amp: 60, speed: 0.4, y: h * 0.3 },
    { color: 'rgba(155,77,255,', freq: 0.004, amp: 40, speed: 0.6, y: h * 0.5 },
    { color: 'rgba(0,229,255,',  freq: 0.0025, amp: 50, speed: 0.3, y: h * 0.7 },
    { color: 'rgba(168,255,62,', freq: 0.005, amp: 25, speed: 0.8, y: h * 0.85 },
  ];

  const scrollOffset = scrollY * 0.3;

  waveConfigs.forEach((cfg, idx) => {
    ctx.beginPath();
    const opacity = 0.08 - idx * 0.015;

    for (let x = 0; x <= w; x += 2) {
      const y = cfg.y
        + Math.sin((x + scrollOffset) * cfg.freq + time * cfg.speed) * cfg.amp
        + Math.sin((x * 0.7 + scrollOffset * 0.8) * cfg.freq * 1.6 + time * cfg.speed * 1.3) * (cfg.amp * 0.4);
      if (x === 0) ctx.moveTo(x, y);
      else ctx.lineTo(x, y);
    }

    ctx.strokeStyle = `${cfg.color}${opacity})`;
    ctx.lineWidth = 1.5;
    ctx.stroke();
  });

  // Subtle horizontal grid lines
  ctx.strokeStyle = 'rgba(30, 30, 56, 0.6)';
  ctx.lineWidth = 1;
  for (let y = 0; y < h; y += 60) {
    ctx.beginPath();
    ctx.moveTo(0, y);
    ctx.lineTo(w, y);
    ctx.stroke();
  }

  // Vertical grid lines
  for (let x = 0; x < w; x += 80) {
    ctx.beginPath();
    ctx.moveTo(x, 0);
    ctx.lineTo(x, h);
    ctx.stroke();
  }
}

// â”€â”€ Main animation loop â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
if (!reduced) {
  let startTime = null;
  function tick(ts) {
    if (!startTime) startTime = ts;
    const elapsed = (ts - startTime) / 1000;
    drawWaveform(elapsed);
    requestAnimationFrame(tick);
  }
  requestAnimationFrame(tick);
}

// â”€â”€ Hero entrance â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
