tl1
  // Step 0: Circle appears
  .to('#vis-circle', { opacity: 1, scale: 1, duration: dur(0.3) }, 0)
  .to('.vis-line-1', { opacity: 1, duration: dur(0.2) }, 0.05)
  // Step 0 â†’ 1: Circle moves, square enters
