// Hero entrance
if (!reduced) {
  gsap.set(['.hero-badge', '.hero-h1', '.hero-prize', '.hero-ctas', '.hero-stats-row'], { opacity: 0, y: 30 });
  gsap.timeline({ delay: 0.3, defaults: { ease: 'expo.out' } })
