// Scroll Fade In â€” Intersection Observer
(function () {
  "use strict";

  const elements = document.querySelectorAll(".fade-in-el");

  if (!elements.length) return;

  // Skip animation if user prefers reduced motion
  if (window.matchMedia("(prefers-reduced-motion: reduce)").matches) {
    elements.forEach((el) => el.classList.add("is-visible"));
    return;
  }

  const observer = new IntersectionObserver(
    (entries, obs) => {
      entries.forEach((entry) => {
        if (!entry.isIntersecting) return;
        entry.target.classList.add("is-visible");
        // Unobserve once visible â€” no need to keep watching
        obs.unobserve(entry.target);
      });
    },
    {
      threshold: 0.15, // trigger when 15% of the element is visible
      rootMargin: "0px 0px -40px 0px", // slight offset from viewport bottom
    }
  );

  elements.forEach((el) => observer.observe(el));
})();

